import React, { useState, useEffect } from "react";
import axios from "axios";

const ProductListing = () => {
    const [products, setProducts] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [priceRange, setPriceRange] = useState(200);
    
    // Temporary state to hold filter values before applying
    const [tempSearchQuery, setTempSearchQuery] = useState("");
    const [tempSelectedCategories, setTempSelectedCategories] = useState([]);
    const [tempPriceRange, setTempPriceRange] = useState(200);
    
    // State to store filtered products (only updates when Apply Filters is clicked)
    const [filteredProducts, setFilteredProducts] = useState([]);

    // Fetch products from the backend API
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/products");
                setProducts(response.data);
                setFilteredProducts(response.data); // Initialize displayed products
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    // Handle category selection
    const handleCategoryChange = (category) => {
        setTempSelectedCategories((prev) =>
            prev.includes(category)
                ? prev.filter((c) => c !== category)
                : [...prev, category]
        );
    };

    // Apply Filters Button Click
    const applyFilters = () => {
        const newFilteredProducts = products.filter((product) => {
            if (!product || typeof product !== "object") return false;

            const matchesSearch = tempSearchQuery
                ? product.name?.toLowerCase().includes(tempSearchQuery.toLowerCase())
                : true;

            const matchesCategory =
                tempSelectedCategories.length === 0 || (product.category && tempSelectedCategories.includes(product.category));

            const matchesPrice = product.price && product.price <= tempPriceRange;

            return matchesSearch && matchesCategory && matchesPrice;
        });

        setSearchQuery(tempSearchQuery);
        setSelectedCategories(tempSelectedCategories);
        setPriceRange(tempPriceRange);
        setFilteredProducts(newFilteredProducts);
    };

    return (
        <div className="min-h-screen bg-gray-50 flex">
            {/* Sidebar with Filters */}
            <aside className="w-1/4 bg-white p-6 shadow-md">
                <h2 className="text-xl font-semibold mb-4">Filters</h2>

                {/* Search Bar */}
                <div className="mb-4">
                    <input
                        type="text"
                        placeholder="Search products..."
                        className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                        value={tempSearchQuery}
                        onChange={(e) => setTempSearchQuery(e.target.value)}
                    />
                </div>

                {/* Category Filters */}
                <div className="mb-4">
                    <h3 className="text-lg font-medium mb-2">Categories</h3>
                    <ul className="space-y-2">
                        {["Shoes", "Sneakers", "Trainers", "Running"].map((category) => (
                            <li key={category}>
                                <input
                                    type="checkbox"
                                    className="mr-2"
                                    checked={tempSelectedCategories.includes(category)}
                                    onChange={() => handleCategoryChange(category)}
                                />
                                {category}
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Price Filter */}
                <div className="mb-4">
                    <h3 className="text-lg font-medium mb-2">Price Range: ${tempPriceRange}</h3>
                    <input
                        type="range"
                        className="w-full cursor-pointer"
                        min="0"
                        max="200"
                        value={tempPriceRange}
                        onChange={(e) => setTempPriceRange(Number(e.target.value))}
                    />
                </div>

                {/* Apply Filters Button */}
                <button
                    className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
                    onClick={applyFilters}
                >
                    Apply Filters
                </button>
            </aside>

            {/* Product Listing Section */}
            <main className="w-3/4 p-6">
                <h1 className="text-3xl font-bold mb-6">Our Products</h1>

                {/* Product Grid */}
                <div className="grid grid-cols-3 gap-6">
                    {filteredProducts.length > 0 ? (
                        filteredProducts.map((product) => (
                            <div key={product._id} className="bg-white p-4 shadow-md rounded-lg">
                                <img
                                    src={product.images?.[0] || "https://via.placeholder.com/150"}
                                    alt={product.name}
                                    className="w-full h-40 object-cover rounded-md"
                                />
                                <h3 className="text-lg font-semibold mt-2">{product.name}</h3>
                                <p className="text-blue-600 font-medium">${product.price}</p>
                                <button className="mt-3 w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition">
                                    Add to Cart
                                </button>
                            </div>
                        ))
                    ) : (
                        <p className="text-center col-span-3 text-gray-500">No products found</p>
                    )}
                </div>
            </main>
        </div>
    );
};

export default ProductListing;
