import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ShoppingCart, Trash2, Minus, Plus, CreditCard, Gem } from "lucide-react";


const Cart = () => {
        const [cartItems, setCartItems] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const navigate = useNavigate();
        const userId = localStorage.getItem("userId");
        const token = localStorage.getItem("userToken");

        const fetchCart = async () => {
                try {
                        const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                                method: "GET",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                        });

                        const data = await response.json();

                        if (response.ok) {
                                setCartItems(data.products || []);
                        } else {
                                setError(data.message || "Failed to load cart.");
                        }
                } catch (err) {
                        console.error("Fetch error:", err);
                        setError("Server error while fetching cart.");
                } finally {
                        setLoading(false);
                }
        };

        useEffect(() => {
                if (userId && token) {
                        fetchCart();
                }
        }, [userId, token]);

        const updateQuantity = async (id, amount) => {
                const newQuantity = cartItems.find((item) => item.product._id === id)?.quantity + amount;
                if (newQuantity < 1) return removeItem(id);

                try {
                        await fetch("http://localhost:5000/api/cart/update", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id, quantity: newQuantity }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Update error:", err);
                }
        };

        const removeItem = async (id) => {
                try {
                        await fetch("http://localhost:5000/api/cart/remove", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Remove error:", err);
                }
        };

        const handleCheckout = () => {
                navigate("/checkout", {
                        state: {
                                cartItems,
                                subtotal,
                                tax,
                                shipping,
                                total
                        }
                });
        };


        const subtotal = cartItems.reduce((acc, item) => acc + item.product.price * item.quantity, 0);
        const tax = subtotal * 0.1;
        const shipping = subtotal > 0 ? 10 : 0;
        const total = subtotal + tax + shipping;

        if (loading) return <div>Loading...</div>;
        if (error) return <div className="text-red-600">{error}</div>;

        return (
                <div className="max-w-7xl mx-auto p-6 mt-24 relative">
                        <div className="absolute top-0 left-1/3 w-96 h-96 bg-gradient-to-br from-[#B76E79]/10 to-[#C5A880]/10 rounded-full blur-3xl -translate-y-1/3" />
                        <div className="absolute bottom-0 right-32 w-64 h-64 bg-gradient-to-tl from-[#FAF6F0] to-[#F5EDE1]/50 rounded-full blur-2xl translate-y-1/2" />

                        <div className="grid grid-cols-1 lg:grid-cols-[1.2fr_0.8fr] gap-8 relative z-10">
                                <div className="space-y-8">
                                        <div className="flex items-center gap-5 mb-10">
                                                <div className="p-4 bg-[#B76E79]/10 rounded-xl shadow-inner">
                                                        <ShoppingCart className="w-9 h-9 text-[#B76E79]" />
                                                </div>
                                                <h2 className="text-4xl font-serif text-[#6B4E4D] tracking-tight">Your Curated Collection</h2>
                                        </div>

                                        {cartItems.length === 0 ? (
                                                <div className="text-center py-16 bg-white/90 backdrop-blur-sm rounded-3xl border border-[#E0D6CC] shadow-xl">
                                                        <div className="inline-block p-6 bg-gradient-to-br from-[#B76E79]/10 to-[#C5A880]/10 rounded-2xl mb-6">
                                                                <Gem className="w-20 h-20 text-[#B76E79]/40" />
                                                        </div>
                                                        <h3 className="text-2xl font-serif text-[#6B4E4D] mb-3">Your Luxury Collection Awaits</h3>
                                                        <p className="text-[#6B4E4D]/80 max-w-md mx-auto mb-8">Discover pieces worthy of your personal gallery - each addition reflects your exquisite taste</p>
                                                        <button
                                                                onClick={() => navigate('/collections')}
                                                                className="bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white px-8 py-3 rounded-xl hover:shadow-lg transition-all"
                                                        >
                                                                Explore Signature Pieces
                                                        </button>
                                                </div>
                                        ) : (
                                                <div className="space-y-6">
                                                        {cartItems.map((item) => (
                                                                <div key={item.product._id} className="group bg-white/90 backdrop-blur-sm p-5 rounded-2xl border-2 border-[#E0D6CC] hover:border-[#B76E79]/40 transition-all duration-300 shadow-sm hover:shadow-md">
                                                                        <div className="flex flex-col sm:flex-row items-start gap-6">
                                                                                <div className="relative w-full sm:w-32 aspect-square rounded-xl overflow-hidden">
                                                                                        <img
                                                                                                src={item.product.image}
                                                                                                alt={item.product.name}
                                                                                                className="w-full h-full object-cover transform transition-transform duration-500 group-hover:scale-105"
                                                                                        />
                                                                                        <div className="absolute inset-0 bg-gradient-to-b from-[#6B4E4D]/10 to-transparent" />
                                                                                </div>

                                                                                <div className="flex-1 w-full space-y-4">
                                                                                        <div className="flex justify-between items-start">
                                                                                                <div>
                                                                                                        <h3 className="text-xl font-serif text-[#6B4E4D]">{item.product.name}</h3>
                                                                                                        <p className="text-[#B76E79] mt-1 text-lg">${item.product.price.toFixed(2)}</p>
                                                                                                </div>
                                                                                                <button
                                                                                                        onClick={() => removeItem(item.product._id)}
                                                                                                        className="text-[#6B4E4D]/70 hover:text-[#B76E79] p-2 hover:bg-[#B76E79]/10 rounded-lg transition-all"
                                                                                                >
                                                                                                        <Trash2 className="w-5 h-5" />
                                                                                                </button>
                                                                                        </div>

                                                                                        <div className="flex items-center justify-between">
                                                                                                <div className="flex items-center gap-3 bg-[#FAF6F0] px-4 py-2 rounded-xl border border-[#E0D6CC]">
                                                                                                        <button
                                                                                                                onClick={() => updateQuantity(item.product._id, -1)}
                                                                                                                className="text-[#6B4E4D] hover:text-[#B76E79] p-1 rounded-md hover:bg-[#B76E79]/10 transition-colors"
                                                                                                        >
                                                                                                                <Minus className="w-5 h-5" />
                                                                                                        </button>
                                                                                                        <span className="text-[#6B4E4D] w-8 text-center font-medium">{item.quantity}</span>
                                                                                                        <button
                                                                                                                onClick={() => updateQuantity(item.product._id, 1)}
                                                                                                                className="text-[#6B4E4D] hover:text-[#B76E79] p-1 rounded-md hover:bg-[#B76E79]/10 transition-colors"
                                                                                                        >
                                                                                                                <Plus className="w-5 h-5" />
                                                                                                        </button>
                                                                                                </div>
                                                                                                <p className="text-lg font-medium text-[#B76E79]">${(item.product.price * item.quantity).toFixed(2)}</p>
                                                                                        </div>
                                                                                </div>
                                                                        </div>
                                                                </div>
                                                        ))}
                                                </div>
                                        )}
                                </div>

                                <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 border border-[#E0D6CC] shadow-xl h-fit sticky top-24 space-y-8">
                                        <div className="flex items-center gap-5 mb-8">
                                                <div className="p-4 bg-[#B76E79]/10 rounded-xl shadow-inner">
                                                        <CreditCard className="w-9 h-9 text-[#B76E79]" />
                                                </div>
                                                <h3 className="text-4xl font-serif text-[#6B4E4D] tracking-tight">Order Summary</h3>
                                        </div>

                                        <div className="space-y-5">
                                                <div className="flex justify-between items-center pb-3 border-b border-[#E0D6CC]">
                                                        <span className="text-[#6B4E4D]/80">Subtotal</span>
                                                        <span className="font-medium text-[#B76E79] text-lg">${subtotal.toFixed(2)}</span>
                                                </div>
                                                <div className="flex justify-between items-center pb-3 border-b border-[#E0D6CC]">
                                                        <div className="text-[#6B4E4D]/80">
                                                                Craftsmanship Fee
                                                                <span className="block text-sm text-[#6B4E4D]/60">Premium artisan costs</span>
                                                        </div>
                                                        <span className="font-medium text-[#B76E79] text-lg">${tax.toFixed(2)}</span>
                                                </div>
                                                <div className="flex justify-between items-center pb-3 border-b border-[#E0D6CC]">
                                                        <div className="text-[#6B4E4D]/80">
                                                                Secure Shipping
                                                                <span className="block text-sm text-[#6B4E4D]/60">Insured & tracked delivery</span>
                                                        </div>
                                                        <span className="font-medium text-[#B76E79] text-lg">${shipping.toFixed(2)}</span>
                                                </div>
                                        </div>

                                        <div className="flex justify-between items-center py-5 border-t border-[#E0D6CC]">
                                                <span className="text-2xl font-serif text-[#6B4E4D]">Total</span>
                                                <span className="text-3xl font-bold text-[#B76E79]">${total.toFixed(2)}</span>
                                        </div>

                                        <button
                                                className={`w-full py-4 rounded-xl font-medium text-lg transition-all flex items-center justify-center gap-3 ${total === 0
                                                                ? "bg-gray-200 text-gray-400 cursor-not-allowed"
                                                                : "bg-gradient-to-br from-[#B76E79] via-[#C5A880] to-[#B76E79] bg-size-200 hover:bg-position-100 text-white hover:shadow-xl"
                                                        }`}
                                                disabled={total === 0}
                                                onClick={handleCheckout}
                                        >
                                                {total === 0 ? (
                                                        <>
                                                                <ShoppingBag className="w-6 h-6" />
                                                                Continue Shopping
                                                        </>
                                                ) : (
                                                        <>
                                                                <Lock className="w-6 h-6" />
                                                                Secure Checkout
                                                        </>
                                                )}
                                        </button>
                                </div>
                        </div>
                </div>
        );
};

export default Cart;
