import React, { useState } from "react";

const Wishlist = () => {
  // Sample Wishlist Items
  const [wishlist, setWishlist] = useState([
    {
      id: 1,
      name: "Gold Pendant Necklace",
      price: 249,
      image: "https://via.placeholder.com/150",
    },
    {
      id: 2,
      name: "Diamond Stud Earrings",
      price: 399,
      image: "https://via.placeholder.com/150",
    },
    {
      id: 3,
      name: "Silver Bracelet",
      price: 199,
      image: "https://via.placeholder.com/150",
    },
  ]);

  const [cart, setCart] = useState([]);

  // Function to move item from Wishlist to Cart
  const addToCart = (item) => {
    setCart([...cart, item]);
    setWishlist(wishlist.filter((wishItem) => wishItem.id !== item.id));
  };

  // Function to remove item from Wishlist
  const removeFromWishlist = (id) => {
    setWishlist(wishlist.filter((item) => item.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
      <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-lg">
        
        {/* Page Title */}
        <h1 className="text-3xl font-semibold text-gray-800 mb-6">Your Wishlist</h1>

        {/* Wishlist Grid */}
        {wishlist.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {wishlist.map((item) => (
              <div key={item.id} className="bg-gray-50 p-4 rounded-lg shadow-md flex flex-col items-center">
                <img src={item.image} alt={item.name} className="w-32 h-32 object-cover rounded-md mb-4" />
                <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                <p className="text-gray-600">${item.price.toFixed(2)}</p>

                {/* Action Buttons */}
                <div className="mt-4 flex space-x-3">
                  <button
                    onClick={() => addToCart(item)}
                    className="bg-amber-600 text-white px-4 py-2 rounded-lg hover:bg-amber-700 transition"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => removeFromWishlist(item.id)}
                    className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-lg text-gray-500">Your wishlist is empty.</p>
            <button className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition">
              Continue Shopping
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Wishlist;
