import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { User, Mail, Phone, MapPin, Save, Camera } from "lucide-react";

const EditProfile = () => {
        const [formData, setFormData] = useState({ name: "", email: "", phone: "", address: "" });
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setFormData({ name: data.name, email: data.email, phone: data.phone, address: data.address });
                        } catch (error) {
                                setMessage("❌ Failed to fetch user profile.");
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleChange = (e) => {
                setFormData({ ...formData, [e.target.name]: e.target.value });
        };

        const handleSubmit = async (e) => {
                e.preventDefault();
                try {
                        const token = localStorage.getItem("userToken");

                        await axios.put("http://localhost:5000/api/users/profile", formData, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setMessage("✅ Profile updated successfully!");

                        setTimeout(() => {
                                setMessage("");
                                navigate("/profile");
                        }, 1500);
                } catch (error) {
                        setMessage("❌ Failed to update profile.");
                }

                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };

        return (
                <div className="relative z-10 w-full max-w-4xl bg-white/90 backdrop-blur-sm rounded-[2rem] border border-[#E0D6CC] shadow-2xl p-12 mt-20 mx-auto">
                        {/* Floating Decorative Elements */}
                        <div className="absolute -top-24 left-1/4 w-48 h-48 bg-gradient-to-r from-[#B76E79]/15 to-[#C5A880]/15 rounded-full blur-2xl"></div>
                        <div className="absolute -bottom-24 right-1/4 w-56 h-56 bg-gradient-to-b from-[#C5A880]/10 to-transparent rounded-full blur-xl"></div>

                        {/* Profile Container */}
                        <div className="flex flex-col lg:flex-row gap-12 items-start">
                                {/* Avatar & Info Section */}
                                <div className="w-full lg:w-1/3 flex flex-col items-center lg:items-start space-y-8">
                                        <div className="relative group">
                                                <div className="absolute -inset-2 bg-gradient-to-br from-[#B76E79]/20 to-[#C5A880]/20 rounded-full blur-lg animate-pulse"></div>
                                                <div className="relative z-10 p-1 rounded-full border-2 border-[#E0D6CC] hover:border-[#B76E79]/50 transition-colors">
                                                        <img
                                                                src={`https://ui-avatars.com/api/?name=${encodeURIComponent(formData.name || "User")}&background=FAF6F0&color=B76E79&size=150`}
                                                                alt="Profile"
                                                                className="w-40 h-40 rounded-full border-4 border-white shadow-lg"
                                                        />
                                                </div>
                                                <button className="absolute bottom-4 right-4 z-20 bg-gradient-to-br from-[#B76E79] to-[#C5A880] p-2.5 rounded-full text-white shadow-lg hover:scale-105 transition-transform">
                                                        <Camera className="w-7 h-7" />
                                                </button>
                                        </div>

                                        <div className="text-center lg:text-left">
                                                <h2 className="text-3xl font-serif text-[#6B4E4D] mb-2">Profile Settings</h2>
                                                {message && <p className="text-[#B76E79] text-sm max-w-xs">{message}</p>}
                                        </div>
                                </div>

                                {/* Form Section */}
                                <form onSubmit={handleSubmit} className="flex-1 w-full space-y-8">
                                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                                {[
                                                        { name: "name", placeholder: "Full Name", icon: <User className="w-6 h-6 text-[#B76E79]" /> },
                                                        { name: "email", placeholder: "Email Address", icon: <Mail className="w-6 h-6 text-[#B76E79]" /> },
                                                        { name: "phone", placeholder: "Contact Number", icon: <Phone className="w-6 h-6 text-[#B76E79]" /> },
                                                        { name: "address", placeholder: "Shipping Address", icon: <MapPin className="w-6 h-6 text-[#B76E79]" /> }
                                                ].map(({ name, placeholder, icon }) => (
                                                        <div key={name} className="relative group">
                                                                <div className="absolute inset-y-0 left-4 flex items-center opacity-80 group-hover:opacity-100 transition-opacity">
                                                                        {icon}
                                                                </div>
                                                                <input
                                                                        className="w-full px-6 py-4 pl-14 bg-[#FAF6F0] border border-[#E0D6CC] rounded-2xl text-[#6B4E4D] placeholder-[#B76E79]/50 focus:ring-2 focus:ring-[#B76E79]/50 focus:border-transparent transition-all hover:border-[#B76E79]/30"
                                                                        name={name}
                                                                        placeholder={placeholder}
                                                                        value={formData[name]}
                                                                        onChange={handleChange}
                                                                />
                                                        </div>
                                                ))}
                                        </div>

                                        {/* Save Button */}
                                        <div className="pt-8 border-t border-[#E0D6CC]">
                                                <button
                                                        type="submit"
                                                        className="w-full bg-gradient-to-r from-[#B76E79] to-[#C5A880] hover:from-[#C5A880] hover:to-[#B76E79] text-white py-5 rounded-2xl font-medium shadow-lg hover:shadow-xl transition-all flex items-center justify-center gap-3 group"
                                                >
                                                        <Save className="w-6 h-6 transition-transform group-hover:scale-110" />
                                                        <span className="tracking-wide">Update Profile</span>
                                                </button>
                                        </div>
                                </form>
                        </div>
                </div>
        );
};

export default EditProfile;
