import { useState } from "react";
import axios from "axios";

const ForgotPassword = () => {
        const [email, setEmail] = useState("");
        const [message, setMessage] = useState("");
        const [error, setError] = useState("");

        const handleSubmit = async (e) => {
                e.preventDefault();
                setMessage("");
                setError("");

                try {
                        const { data } = await axios.post("http://localhost:5000/api/users/forgot-password", { email });
                        setMessage(data.message);
                } catch (err) {
                        setError(err.response?.data?.message || "Error sending reset email.");
                }
        };

        return (
                <div className="flex items-center justify-center min-h-screen bg-beige">
                        <div className="w-full max-w-md p-6 bg-white shadow-lg rounded-2xl border border-rose-300">
                                <h2 className="text-2xl font-semibold text-rose-600 text-center">Forgot Password</h2>
                                <form onSubmit={handleSubmit} className="mt-4">
                                        <div className="mb-4">
                                                <input
                                                        type="email"
                                                        placeholder="Enter your email"
                                                        value={email}
                                                        onChange={(e) => setEmail(e.target.value)}
                                                        required
                                                        className="w-full p-3 border border-rose-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                                                />
                                        </div>
                                        <button
                                                type="submit"
                                                className="w-full bg-rose-500 text-white font-semibold py-3 rounded-lg shadow-md hover:bg-rose-600 transition duration-300"
                                        >
                                                Send Reset Link
                                        </button>
                                </form>
                                {message && <p className="mt-4 text-green-600 text-center">{message}</p>}
                                {error && <p className="mt-4 text-red-600 text-center">{error}</p>}
                        </div>
                </div>

        );
};

export default ForgotPassword;
