import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link from React Router
import axios from "axios";

const ProductListing = () => {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]); // Fetch categories dynamically
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [priceRange, setPriceRange] = useState(20000);
    const [message, setMessage] = useState("");

    useEffect(() => {
        const fetchProducts = async (id) => {
            try {
                const response = await axios.get("http://localhost:5000/api/products");
                setProducts(response.data);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        const fetchCategories = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/categories");
                setCategories(response.data); // Assume backend returns an array of category names
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };

        fetchProducts();
        fetchCategories();
    }, []);

    const handleAddToCart = async (id) => {  // Accept 'id' as an argument
        const userId = localStorage.getItem("userId");
        const token = localStorage.getItem("userToken");
    
        if (!userId || !token) {
            setMessage("Please log in to add items to cart.");
            return;
        }
    
        console.log("Sending request to add product:", { userId, productId: id });
    
        try {
            const response = await fetch("http://localhost:5000/api/cart/add", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ userId, productId: id }),
            });
    
            const data = await response.json();
            console.log("Response from server:", data);
    
            if (response.ok) {
                setMessage("Added to cart successfully!");
            } else {
                setMessage(data.message || "Error adding to cart");
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("Server error while adding to cart");
        }
    };
    


    const handleCategoryChange = (category) => {
        setSelectedCategories((prevCategories) =>
            prevCategories.includes(category)
                ? prevCategories.filter((c) => c !== category)
                : [...prevCategories, category]
        );
    };

    const filteredProducts = products.filter((product) => {
        const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(product.category);
        const matchesPrice = product.price <= priceRange;
        return matchesSearch && matchesCategory && matchesPrice;
    });

    return (
        <div className="min-h-screen bg-gray-50 flex">
            <aside className="w-1/4 bg-white p-6 shadow-md">
                <h2 className="text-xl font-semibold mb-4">Filters</h2>
                <div className="mb-4">
                    <input
                        type="text"
                        placeholder="Search products..."
                        className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <h3 className="text-lg font-medium mb-2">Categories</h3>
                    {/* // Make sure you are mapping correctly to render category names */}
                    <ul className="space-y-2">
                        {categories.length > 0 ? (
                            categories.map((category) => (
                                <li key={category._id}>
                                    <input
                                        type="checkbox"
                                        className="mr-2"
                                        checked={selectedCategories.includes(category.name)} // Use category name here
                                        onChange={() => handleCategoryChange(category.name)} // Use category name here
                                    />
                                    {category.name} {/* Render the category name */}
                                </li>
                            ))
                        ) : (
                            <p className="text-gray-500">Loading categories for you.</p>
                        )}
                    </ul>

                </div>
                <div>
                    <h3 className="text-lg font-medium mb-2">Max Price Range: ${priceRange}</h3>
                    <input
                        type="range"
                        className="w-full cursor-pointer"
                        min="0"
                        max="20000"
                        value={priceRange}
                        onChange={(e) => setPriceRange(e.target.value)}
                    />
                </div>
            </aside>

            <main className="w-3/4 p-6">
                <h1 className="text-3xl font-bold mb-6">Our Products</h1>
                <div className="grid grid-cols-3 gap-6">
                    {filteredProducts.length > 0 ? (
                        filteredProducts.map((product) => (
                            <div key={product._id} className="bg-white p-4 shadow-md rounded-lg">
                                <img src={product.images[0]} alt={product.name} className="w-full h-40 object-cover rounded-md" />
                                <h3 className="text-lg font-semibold mt-2">{product.name}</h3>
                                <p className="text-blue-600 font-medium">${product.price}</p>
                                <button onClick={() => handleAddToCart(product._id)} className="mt-4 bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition duration-300">
                                    Add to Cart
                                </button>
                                <Link to={`/product/${product._id}`} className="block mt-2 text-center text-blue-500 hover:underline">
                                    View Product
                                </Link>

                            </div>
                        ))
                    ) : (
                        <p className="text-center col-span-3 text-gray-500">Rendering Jewels for you.</p>
                    )}
                </div>
            </main>
        </div>
    );
};

export default ProductListing;
