import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const ProductListing = () => {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]); // Dynamic categories
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [priceRange, setPriceRange] = useState(200);
    const [appliedFilters, setAppliedFilters] = useState(false); // Track filter application

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/products");
                setProducts(response.data);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        const fetchCategories = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/categories");
                setCategories(response.data);
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };

        fetchProducts();
        fetchCategories();
    }, []);

    const handleCategoryChange = (category) => {
        setSelectedCategories((prevCategories) =>
            prevCategories.includes(category)
                ? prevCategories.filter((c) => c !== category)
                : [...prevCategories, category]
        );
    };

    const applyFilters = () => {
        setAppliedFilters(true); // Only apply filtering when the button is clicked
    };

    const filteredProducts = appliedFilters
        ? products.filter((product) => {
            const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
            const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(product.category);
            const matchesPrice = product.price <= priceRange;
            return matchesSearch && matchesCategory && matchesPrice;
        })
        : products;

    return (
        <div className="min-h-screen bg-gray-50 flex">
            <aside className="w-1/4 bg-white p-6 shadow-md">
                <h2 className="text-xl font-semibold mb-4">Filters</h2>
                <div className="mb-4">
                    <input
                        type="text"
                        placeholder="Search products..."
                        className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <h3 className="text-lg font-medium mb-2">Categories</h3>
                    <ul className="space-y-2">
                        {categories.map((category) => (
                            <li key={category._id}>
                                <input
                                    type="checkbox"
                                    className="mr-2"
                                    checked={selectedCategories.includes(category.name)}
                                    onChange={() => handleCategoryChange(category._id)}
                                />
                                {category.name}
                            </li>
                        ))}
                    </ul>
                </div>
                <div>
                    <h3 className="text-lg font-medium mb-2">Price Range: ${priceRange}</h3>
                    <input
                        type="range"
                        className="w-full cursor-pointer"
                        min="0"
                        max="20000"
                        value={priceRange}
                        onChange={(e) => setPriceRange(e.target.value)}
                    />
                </div>
                <button
                    className="mt-4 w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
                    onClick={applyFilters}
                >
                    Apply Filters
                </button>
            </aside>

            <main className="w-3/4 p-6">
                <h1 className="text-3xl font-bold mb-6">Our Products</h1>
                <div className="grid grid-cols-3 gap-6">
                    {filteredProducts.length > 0 ? (
                        filteredProducts.map((product) => (
                            <div key={product._id} className="bg-white p-4 shadow-md rounded-lg">
                                <img src={product.images[0]} alt={product.name} className="w-full h-40 object-cover rounded-md" />
                                <h3 className="text-lg font-semibold mt-2">{product.name}</h3>
                                <p className="text-blue-600 font-medium">${product.price}</p>
                                <button className="mt-3 w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition">
                                    Add to Cart
                                </button>
                                <Link to={`/product/${product._id}`} className="block mt-2 text-center text-blue-500 hover:underline">
                                    View Product
                                </Link>
                            </div>
                        ))
                    ) : (
                        <p className="text-center col-span-3 text-gray-500">No products found</p>
                    )}
                </div>
            </main>
        </div>
    );
};

export default ProductListing;
