import React, { useState, useEffect } from "react";

const Cart = () => {
    const [cartItems, setCartItems] = useState([]); // Stores fetched cart data
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const userId = localStorage.getItem("userId"); // Get userId from localStorage
    const token = localStorage.getItem("userToken"); // Get JWT token

    // Fetch cart items from backend
    useEffect(() => {
        if (!userId || !token) {
            setError("Please log in to view your cart.");
            setLoading(false);
            return;
        }

        const fetchCart = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setCartItems(data.products || []);
                } else {
                    setError(data.message || "Failed to load cart.");
                }
            } catch (err) {
                setError("Server error while fetching cart.");
            } finally {
                setLoading(false);
            }
        };

        fetchCart();
    }, [userId, token]);

    // Function to update quantity (Increase/Decrease)
    const updateQuantity = async (productId, action) => {
        const endpoint = action === "increase" ? "/increase" : "/decrease";

        try {
            const response = await fetch(`http://localhost:5000/api/cart${endpoint}`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ userId, productId }),
            });

            const data = await response.json();
            if (response.ok) {
                setCartItems(data.cart.products || []); // Update cart items with latest data
            } else {
                setError(data.message || "Error updating cart.");
            }
        } catch (err) {
            setError("Server error while updating cart.");
        }
    };

    // Function to remove an item from cart
    const removeItem = async (productId) => {
        try {
            const response = await fetch(`http://localhost:5000/api/cart/remove`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ userId, productId }),
            });

            const data = await response.json();
            if (response.ok) {
                setCartItems(data.cart.products || []); // Update cart items after removal
            } else {
                setError(data.message || "Error removing item.");
            }
        } catch (err) {
            setError("Server error while removing item.");
        }
    };

    // Calculate total price
    const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
    const tax = subtotal * 0.1;
    const total = subtotal + tax;

    if (loading) return <div className="text-center py-10">Loading...</div>;
    if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

    return (
        <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
            <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-lg flex flex-col md:flex-row gap-6">
                
                {/* Left Side: Cart Items List */}
                <div className="w-full md:w-2/3 bg-gray-50 p-6 rounded-lg shadow-md">
                    <h1 className="text-3xl font-semibold text-gray-800 mb-6">Your Shopping Cart</h1>
                    {cartItems.length > 0 ? (
                        <div className="space-y-4">
                            {cartItems.map((item) => (
                                <div key={item.product._id} className="flex items-center justify-between bg-white p-4 rounded-lg shadow">
                                    <div className="flex items-center space-x-4">
                                        <img src={item.product.image || "https://via.placeholder.com/100"} alt={item.product.name} className="w-20 h-20 object-cover rounded-md" />
                                        <div>
                                            <h2 className="text-lg font-semibold text-gray-800">{item.product.name}</h2>
                                            <p className="text-gray-600">${item.price.toFixed(2)}</p>
                                        </div>
                                    </div>

                                    {/* Quantity Selector */}
                                    <div className="flex items-center space-x-2">
                                        <button
                                            onClick={() => updateQuantity(item.product._id, "decrease")}
                                            className="px-3 py-1 bg-gray-300 rounded hover:bg-gray-400"
                                        >
                                            âˆ’
                                        </button>
                                        <span className="px-3 py-1 border rounded">{item.quantity}</span>
                                        <button
                                            onClick={() => updateQuantity(item.product._id, "increase")}
                                            className="px-3 py-1 bg-gray-300 rounded hover:bg-gray-400"
                                        >
                                            +
                                        </button>
                                    </div>

                                    {/* Remove Button */}
                                    <button
                                        onClick={() => removeItem(item.product._id)}
                                        className="text-red-500 hover:text-red-700 transition font-medium"
                                    >
                                        Remove
                                    </button>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div className="text-center py-12">
                            <p className="text-lg text-gray-500">Your cart is empty.</p>
                            <button className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition">
                                Continue Shopping
                            </button>
                        </div>
                    )}
                </div>

                {/* Right Side: Cart Summary */}
                <div className="w-full md:w-1/3 bg-gray-50 p-6 rounded-lg shadow-md">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-4">Cart Summary</h2>
                    <div className="space-y-2 text-gray-600">
                        <p>Subtotal: <span className="float-right">${subtotal.toFixed(2)}</span></p>
                        <p>Tax (10%): <span className="float-right">${tax.toFixed(2)}</span></p>
                        <hr />
                        <p className="text-lg font-semibold">Total: <span className="float-right text-gray-900">${total.toFixed(2)}</span></p>
                    </div>

                    {/* Checkout Buttons */}
                    <div className="mt-6 flex flex-col space-y-3">
                        <button className="bg-gray-300 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-400 transition">
                            Continue Shopping
                        </button>
                        <button className="bg-amber-600 text-white px-6 py-2 rounded-lg shadow hover:bg-amber-700 transition">
                            Proceed to Checkout
                        </button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default Cart;
