import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
        const [wishlist, setWishlist] = useState([]);
        const [loading, setLoading] = useState(true);
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchWishlist = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        alert("Please log in to view your wishlist.");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/wishlist", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setWishlist(data.wishlist?.products || []);
                        } catch (error) {
                                setMessage(error.response?.data?.message || "Failed to fetch wishlist.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchWishlist();
        }, []);

        const removeFromWishlist = async (productId) => {
                try {
                        const token = localStorage.getItem("userToken");
                        await axios.delete(`http://localhost:5000/api/wishlist/remove/${productId}`, {
                                headers: { Authorization: `Bearer ${token}` },
                        });
                        setWishlist(wishlist.filter((item) => item._id !== productId));
                } catch (error) {
                        setMessage(error.response?.data?.message || "Failed to remove item from wishlist.");
                }
        };

        if (loading) return <div className="text-center text-gray-600 py-10">Loading wishlist...</div>;

        return (
                <div className="container mx-auto px-6 py-16">
                        <h1 className="text-4xl font-bold text-center text-gray-800 mb-10">Your Wishlist</h1>
                        {wishlist.length > 0 ? (
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                                        {wishlist.map((item) => (
                                                <div key={item._id} className="bg-white rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 p-5 relative">
                                                        <img
                                                                src={item.image || "https://via.placeholder.com/150"}
                                                                alt={item.name}
                                                                className="w-full h-52 object-cover rounded-t-lg"
                                                        />
                                                        <div className="p-5">
                                                                <h4 className="text-2xl font-semibold text-gray-900">{item.name}</h4>
                                                                <p className="text-lg text-gray-600 mt-2">${item.price?.toFixed(2)}</p>
                                                                <div className="flex justify-between items-center mt-6">
                                                                        <button
                                                                                onClick={() => navigate(`/product/${item._id}`)}
                                                                                className="bg-blue-600 text-white font-medium px-5 py-2 rounded-lg hover:bg-blue-700 transition"
                                                                        >
                                                                                View Product
                                                                        </button>
                                                                        <button
                                                                                onClick={() => removeFromWishlist(item._id)}
                                                                                className="bg-red-500 text-white font-medium px-5 py-2 rounded-lg hover:bg-red-600 transition"
                                                                        >
                                                                                Remove
                                                                        </button>
                                                                </div>
                                                        </div>
                                                </div>
                                        ))}
                                </div>
                        ) : (
                                <div className="text-center py-20">
                                        <p className="text-2xl text-gray-500">Your wishlist is empty.</p>
                                </div>
                        )}
                        {message && <p className="text-center text-red-500 mt-6">{message}</p>}
                </div>
        );
};

export default Wishlist;
