import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Signup = () => {
        const [name, setName] = useState("");
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [confirmPassword, setConfirmPassword] = useState("");
        const [error, setError] = useState("");
        const [loading, setLoading] = useState(false);
        const navigate = useNavigate();

        // Handle form submission
        const handleSubmit = async (e) => {
                e.preventDefault();

                if (password !== confirmPassword) {
                        setError("Passwords do not match");
                        return;
                }

                setLoading(true);

                const newUser = { name, email, password };

                try {
                        const response = await fetch("http://localhost:5000/api/users/register", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                },
                                body: JSON.stringify(newUser),
                        });

                        const data = await response.json();

                        if (response.ok) {
                                navigate("/login"); // Redirect to login page
                        } else {
                                setError(data.message || "Registration failed");
                        }
                } catch (err) {
                        console.error(err);
                        setError("Server error");
                } finally {
                        setLoading(false);
                }
        };

        return (
                <div className="flex items-center justify-center min-h-screen bg-[#F5ECE3]">
                        <div className="w-full max-w-lg bg-white shadow-xl rounded-3xl p-8 border border-[#D8B6A4] flex flex-col items-center">
                                <h2 className="text-3xl font-bold text-[#B88E8D] mb-2">Create an Account</h2>
                                <p className="text-gray-600 text-center mb-6">Join us today!</p>

                                <form className="w-full space-y-4" onSubmit={handleSubmit}>
                                        <div className="w-full">
                                                <label className="block text-gray-600 text-sm mb-1">Full Name</label>
                                                <div className="relative">
                                                        <input
                                                                type="text"
                                                                value={name}
                                                                onChange={(e) => setName(e.target.value)}
                                                                className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                                placeholder="Enter your full name"
                                                                required
                                                        />
                                                        <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">ðŸ‘¤</span>
                                                </div>
                                        </div>

                                        <div className="w-full">
                                                <label className="block text-gray-600 text-sm mb-1">Email</label>
                                                <div className="relative">
                                                        <input
                                                                type="email"
                                                                value={email}
                                                                onChange={(e) => setEmail(e.target.value)}
                                                                className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                                placeholder="Enter your email"
                                                                required
                                                        />
                                                        <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">ðŸ“§</span>
                                                </div>
                                        </div>

                                        <div className="w-full">
                                                <label className="block text-gray-600 text-sm mb-1">Password</label>
                                                <div className="relative">
                                                        <input
                                                                type="password"
                                                                value={password}
                                                                onChange={(e) => setPassword(e.target.value)}
                                                                className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                                placeholder="Create a password"
                                                                required
                                                        />
                                                        <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">ðŸ”’</span>
                                                </div>
                                        </div>

                                        <div className="w-full">
                                                <label className="block text-gray-600 text-sm mb-1">Confirm Password</label>
                                                <div className="relative">
                                                        <input
                                                                type="password"
                                                                value={confirmPassword}
                                                                onChange={(e) => setConfirmPassword(e.target.value)}
                                                                className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                                placeholder="Confirm your password"
                                                                required
                                                        />
                                                        <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">âœ…</span>
                                                </div>
                                        </div>

                                        {error && <p className="text-red-500 text-sm text-center">{error}</p>}

                                        <button
                                                type="submit"
                                                className="w-full bg-[#B88E8D] text-white font-semibold py-3 rounded-lg shadow-md hover:bg-[#A17A7A] transition duration-300"
                                                disabled={loading}
                                        >
                                                {loading ? "Signing Up..." : "Sign Up"}
                                        </button>
                                </form>

                                <p className="text-center text-gray-600 text-sm mt-4">
                                        Already have an account?{" "}
                                        <Link to="/login" className="text-[#B88E8D] hover:underline">
                                                Login
                                        </Link>
                                </p>
                        </div>
                </div>

        );
};

export default Signup;
