import React, { useState, useEffect } from 'react';
import CollectionCard from './CollectionCard';

const CollectionGrid = ({ collections, categories, isLoading, error }) => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('newest');
  const [gridLayout, setGridLayout] = useState([]);

  // Filter collections based on selected category
  const filteredCollections = collections?.filter(collection => 
    selectedCategory === 'all' || collection.category?._id === selectedCategory
  ) || [];

  // Sort collections
  const sortedCollections = [...filteredCollections].sort((a, b) => {
    switch (sortBy) {
      case 'newest':
        return new Date(b.createdAt) - new Date(a.createdAt);
      case 'oldest':
        return new Date(a.createdAt) - new Date(b.createdAt);
      case 'name-asc':
        return a.name.localeCompare(b.name);
      case 'name-desc':
        return b.name.localeCompare(a.name);
      default:
        return 0;
    }
  });

  // Generate dynamic grid layout
  useEffect(() => {
    if (sortedCollections.length > 0) {
      const layouts = [
        // Layout patterns for different collection counts
        [
          { width: 'full', height: 'h-96' }, // Featured collection
          { width: 'md:w-1/2', height: 'h-80' },
          { width: 'md:w-1/2', height: 'h-80' },
          { width: 'md:w-1/3', height: 'h-72' },
          { width: 'md:w-2/3', height: 'h-72' },
          { width: 'md:w-1/3', height: 'h-72' },
          { width: 'md:w-1/2', height: 'h-80' },
          { width: 'md:w-1/2', height: 'h-80' },
        ],
        [
          { width: 'md:w-2/3', height: 'h-96' },
          { width: 'md:w-1/3', height: 'h-96' },
          { width: 'md:w-1/3', height: 'h-80' },
          { width: 'md:w-1/3', height: 'h-80' },
          { width: 'md:w-1/3', height: 'h-80' },
        ],
        [
          { width: 'md:w-1/2', height: 'h-96' },
          { width: 'md:w-1/2', height: 'h-96' },
          { width: 'md:w-1/3', height: 'h-80' },
          { width: 'md:w-2/3', height: 'h-80' },
        ]
      ];

      // Select a random layout pattern
      const selectedLayout = layouts[Math.floor(Math.random() * layouts.length)];
      
      // Map collections to layout
      const newLayout = sortedCollections.map((collection, index) => {
        const layoutIndex = index % selectedLayout.length;
        return {
          ...collection,
          layout: selectedLayout[layoutIndex]
        };
      });

      setGridLayout(newLayout);
    }
  }, [sortedCollections]);

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {[...Array(6)].map((_, index) => (
          <div key={index} className="bg-white rounded-lg shadow-md p-4 animate-pulse">
            <div className="bg-gray-200 h-48 rounded-lg mb-4"></div>
            <div className="h-6 bg-gray-200 rounded w-3/4 mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-full mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-2/3"></div>
          </div>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500 mb-4">{error}</p>
        <button 
          onClick={() => window.location.reload()} 
          className="bg-primary-600 text-white px-4 py-2 rounded hover:bg-primary-700"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filters and Sort */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 bg-white p-4 rounded-lg shadow-sm">
        <div className="flex items-center gap-4">
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
          >
            <option value="all">All Categories</option>
            {categories?.map(category => (
              <option key={category._id} value={category._id}>
                {category.name}
              </option>
            ))}
          </select>

          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
          >
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="name-asc">Name (A-Z)</option>
            <option value="name-desc">Name (Z-A)</option>
          </select>
        </div>

        <div className="text-sm text-gray-600">
          {sortedCollections.length} {sortedCollections.length === 1 ? 'Collection' : 'Collections'}
        </div>
      </div>

      {/* Dynamic Collections Grid */}
      {gridLayout.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {gridLayout.map((collection, index) => (
            <div 
              key={collection._id} 
              className={`${collection.layout.width} ${index === 0 ? 'md:col-span-3' : ''}`}
            >
              <div className={`h-full ${collection.layout.height}`}>
                <CollectionCard 
                  collection={collection} 
                  isFeatured={index === 0}
                />
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-8 bg-white rounded-lg shadow-sm">
          <p className="text-gray-600">No collections found</p>
        </div>
      )}
    </div>
  );
};

export default CollectionGrid;
