import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [error, setError] = useState("");
        const [loading, setLoading] = useState(false);
        const navigate = useNavigate();

        const handleSubmit = async (e) => {
                e.preventDefault();
                setLoading(true);
                setError("");

                try {
                        const response = await fetch("http://localhost:5000/api/users/login", {
                                method: "POST",
                                headers: { "Content-Type": "application/json" },
                                body: JSON.stringify({ email, password }),
                        });

                        const data = await response.json();

                        if (response.ok) {
                                localStorage.setItem("userId", data.user.id);
                                localStorage.setItem("userToken", data.user.token); // ✅ Fix: Ensure token is stored
                                window.dispatchEvent(new Event("storage")); // ✅ Fix: Notify auth state update
                                navigate("/"); // ✅ Redirect user after login
                        } else {
                                setError(data.message || "Invalid credentials");
                        }
                } catch (err) {
                        setError("Server error");
                } finally {
                        setLoading(false);
                }
        };


        return (
                <div className="flex items-center justify-center min-h-screen bg-gray-100">
                        <div className="w-full max-w-md p-8 space-y-6 bg-white shadow-lg rounded-lg">
                                <h2 className="text-2xl font-semibold text-center text-gray-700">
                                        Welcome Back
                                </h2>
                                <p className="text-center text-gray-500">Sign in to continue</p>

                                <form className="space-y-4" onSubmit={handleSubmit}>
                                        <div>
                                                <label className="block text-gray-600 text-sm mb-1">Email</label>
                                                <input
                                                        type="email"
                                                        onChange={(e) => setEmail(e.target.value)}
                                                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
                                                        placeholder="Enter your email"
                                                        required
                                                />
                                        </div>

                                        <div>
                                                <label className="block text-gray-600 text-sm mb-1">Password</label>
                                                <input
                                                        type="password"
                                                        onChange={(e) => setPassword(e.target.value)}
                                                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
                                                        placeholder="Enter your password"
                                                        required
                                                />
                                        </div>

                                        {error && <p className="text-red-500 text-sm">{error}</p>}


                                        <div className="flex justify-between text-sm">
                                                <label className="flex items-center space-x-2">
                                                        <input type="checkbox" className="accent-indigo-500" />
                                                        <span>Remember me</span>
                                                </label>
                                                <Link to="/forgot-password" className="forgot-password-link">
  Forgot Password?
</Link>
                                        </div>

                                        <button
                                                type="submit"
                                                className="w-full bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700 transition"
                                                disabled={loading}
                                        >
                                                {loading ? "Logging in..." : "Login"}
                                        </button>
                                </form>

                                <p className="text-center text-gray-600 text-sm">
                                        Don't have an account?{" "}
                                        <Link to="/signup" className="text-indigo-600 hover:underline">
                                                Sign up
                                        </Link>
                                </p>
                        </div>
                </div>
        );
};

export default Login;
