import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
    const { id } = useParams(); // Extract product ID from URL
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [message, setMessage] = useState(""); // State for feedback messages

    // Fetch product details & reviews
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                const data = await response.json();
                if (response.ok) {
                    setProduct(data);
                } else {
                    setError(data.message || "Product not found");
                }
            } catch (err) {
                setError("Product not found or server error");
            } finally {
                setLoading(false);
            }
        };

        const fetchReviews = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/reviews/product/${id}`);
                setReviews(response.data);
            } catch (err) {
                setError(err.response?.data?.message || "Error fetching reviews.");
            }
        };

        fetchProduct();
        fetchReviews();
    }, [id]);

    // Function to hide message after 3 seconds
    const hideMessage = () => {
        setTimeout(() => {
            setMessage("");
        }, 3000);
    };

    if (loading) return <div className="text-center py-10">Loading...</div>;
    if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

    return (
        <div className="relative min-h-screen bg-[#f7f3ee] overflow-hidden px-6 py-[70px]">
            {product && (
                <div className="bg-white mx-auto shadow-xl rounded-tr-3xl rounded-tl-3xl overflow-hidden flex flex-col lg:flex-row gap-8 pt-[50px] p-6">
                    {/* Product Image Section */}
                    <div className="lg:w-1/2 relative flex items-center justify-center">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-auto object-cover rounded-2xl shadow-lg"
                        />
                        <span className="absolute top-4 left-4 bg-[#E4A5A5] text-white px-6 py-3 rounded-full text-sm shadow-md">
                            Luxury Collection
                        </span>
                    </div>

                    {/* Product Details Section */}
                    <div className="lg:w-1/2 flex flex-col justify-between space-y-6">
                        <div>
                            <h2 className="text-4xl font-bold text-gray-900 tracking-wide">{product.name}</h2>
                            <p className="text-lg text-gray-600 mt-4 leading-relaxed">{product.description}</p>
                            <div className="mt-6">
                                <span className="text-3xl font-semibold text-[#E4A5A5]">${product.price}</span>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Product Reviews Section */}
            <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-lg mt-10">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Customer Reviews</h2>

                {reviews.length > 0 ? (
                    reviews.map((review) => (
                        <div key={review._id} className="border-b border-gray-200 py-4">
                            <div className="flex items-center gap-4">
                                <div className="bg-blue-500 text-white w-10 h-10 flex items-center justify-center rounded-full font-bold">
                                    {review.user?.name?.charAt(0) || "U"}
                                </div>
                                <div>
                                    <h3 className="font-semibold text-gray-800">{review.user?.name || "Anonymous"}</h3>
                                    <p className="text-sm text-gray-500">{new Date(review.createdAt).toLocaleDateString()}</p>
                                </div>
                            </div>

                            <div className="mt-2">
                                <div className="flex text-yellow-500">
                                    {Array.from({ length: 5 }).map((_, index) => (
                                        <svg
                                            key={index}
                                            xmlns="http://www.w3.org/2000/svg"
                                            fill={index < review.rating ? "currentColor" : "none"}
                                            viewBox="0 0 24 24"
                                            stroke="currentColor"
                                            className="w-5 h-5"
                                        >
                                            <path
                                                strokeLinecap="round"
                                                strokeLinejoin="round"
                                                strokeWidth="2"
                                                d="M12 4.5l2.8 5.7 6.3.9-4.5 4.4 1.1 6.3-5.7-3-5.7 3 1.1-6.3-4.5-4.4 6.3-.9L12 4.5z"
                                            />
                                        </svg>
                                    ))}
                                </div>
                                <p className="mt-2 text-gray-700">{review.comment}</p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="text-gray-500">No reviews yet. Be the first to review this product!</p>
                )}
            </div>
        </div>
    );
};

export default SingleProduct;
