import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CollectionGrid from '../components/CollectionGrid';
import { motion } from 'framer-motion';

const CollectionsPage = () => {
  const [collections, setCollections] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        setError(null);

        // Fetch collections and categories in parallel
        const [collectionsRes, categoriesRes] = await Promise.all([
          axios.get(`${process.env.REACT_APP_API_URL}/api/collections`),
          axios.get(`${process.env.REACT_APP_API_URL}/api/categories`)
        ]);

        setCollections(collectionsRes.data.collections);
        setCategories(categoriesRes.data.categories);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch collections');
        console.error('Error fetching collections:', err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="container mx-auto px-4 py-8"
    >
      {/* Hero Section */}
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          Our Collections
        </h1>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Discover our carefully curated collections of exquisite jewelry pieces,
          each telling its own unique story of craftsmanship and elegance.
        </p>
      </div>

      {/* Featured Collections Section */}
      {!isLoading && collections.some(c => c.featured) && (
        <div className="mb-12">
          <h2 className="text-2xl font-semibold text-gray-900 mb-6">
            Featured Collections
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {collections
              .filter(c => c.featured)
              .slice(0, 2)
              .map(collection => (
                <div
                  key={collection._id}
                  className="relative rounded-lg overflow-hidden h-64 group"
                >
                  <img
                    src={collection.image}
                    alt={collection.name}
                    className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 to-transparent">
                    <div className="absolute bottom-0 left-0 p-6 text-white">
                      <h3 className="text-2xl font-bold mb-2">{collection.name}</h3>
                      <p className="text-sm opacity-90 line-clamp-2">
                        {collection.description}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}

      {/* All Collections Section */}
      <div>
        <h2 className="text-2xl font-semibold text-gray-900 mb-6">
          All Collections
        </h2>
        <CollectionGrid
          collections={collections}
          categories={categories}
          isLoading={isLoading}
          error={error}
        />
      </div>
    </motion.div>
  );
};

export default CollectionsPage;
