import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const [message, setMessage] = useState(""); // State for feedback messages


        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError(data.message || "Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        } finally {
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);


        // Function to add product to cart
        const handleAddToCart = async () => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        hideMessage();
                        return;
                }

                console.log("Sending request to add product:", { userId, productId: id });

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();
                        console.log("Response from server:", data);

                        if (response.ok) {
                                setMessage("✅ Added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                hideMessage();
        };

        const handleAddToWishlist = async () => {
                const token = localStorage.getItem("userToken");

                if (!token) {
                        setMessage("⚠️ Please log in to add items to your wishlist.");
                        hideMessage();
                        return;
                }

                try {
                        const response = await axios.post(
                                `http://localhost:5000/api/wishlist/add/${id}`,
                                {},
                                {
                                        headers: { Authorization: `Bearer ${token}` },
                                }
                        );

                        console.log("Wishlist response:", response.data);
                        setMessage("❤️ Added to wishlist!");
                } catch (error) {
                        setMessage(`❌ ${error.response?.data?.message || "Failed to add to wishlist."}`);
                }

                hideMessage();
        };

        // Function to hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };



        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="max-w-7xl mx-auto px-6 py-16 bg-[#F5ECE3]">
                        {product && (
                                <div className="bg-white shadow-xl rounded-xl overflow-hidden flex flex-col lg:flex-row">
                                        {/* Product Image Section */}
                                        <div className="lg:w-1/2 relative flex items-center justify-center p-8">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full max-h-[450px] object-cover rounded-lg shadow-lg"
                                                />
                                                <span className="absolute top-4 left-4 bg-[#B88E8D] text-white px-4 py-2 rounded-full text-sm shadow-md">
                                                        Luxury Collection
                                                </span>
                                        </div>

                                        {/* Product Details Section */}
                                        <div className="lg:w-1/2 p-10 flex flex-col justify-between">
                                                <div>
                                                        <h2 className="text-4xl font-bold text-gray-900 tracking-wide">{product.name}</h2>
                                                        <p className="text-lg text-gray-600 mt-4 leading-relaxed">{product.description}</p>
                                                        <div className="mt-6">
                                                                <span className="text-3xl font-semibold text-[#B88E8D]">${product.price}</span>
                                                        </div>
                                                </div>

                                                {/* Action Buttons */}
                                                <div className="mt-6 flex flex-col sm:flex-row sm:space-x-4">
                                                        <button
                                                                onClick={handleAddToCart}
                                                                className="bg-[#B88E8D] text-white py-3 px-8 rounded-lg shadow-md hover:bg-opacity-90 transition-all">
                                                                Add to Cart
                                                        </button>
                                                        <button
                                                                onClick={handleAddToWishlist}
                                                                className="mt-4 sm:mt-0 border-2 border-[#B88E8D] text-[#B88E8D] py-3 px-8 rounded-lg hover:bg-[#B88E8D] hover:text-white transition-all">
                                                                Add to Wishlist
                                                        </button>
                                                </div>
                                                {message && <p className="text-green-600 mt-4">{message}</p>}
                                        </div>
                                </div>
                        )}

                        {/* Related Products Section */}
                        <div className="mt-16">
                                <h3 className="text-2xl font-semibold text-gray-900 mb-6">You May Also Like</h3>
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                                        {product?.recommendedProducts?.map((item, index) => (
                                                <div key={index} className="bg-white rounded-lg shadow-lg overflow-hidden">
                                                        <img
                                                                src={item.image || "https://via.placeholder.com/300"}
                                                                alt={item.name}
                                                                className="w-full h-52 object-cover"
                                                        />
                                                        <div className="p-4">
                                                                <h4 className="text-xl font-medium text-gray-900">{item.name}</h4>
                                                                <p className="text-gray-600 mt-2">${item.price}</p>
                                                                <button className="mt-4 bg-[#B88E8D] text-white py-2 px-4 rounded-lg hover:bg-opacity-90 transition-all">
                                                                        Add to Cart
                                                                </button>
                                                        </div>
                                                </div>
                                        ))}
                                </div>
                        </div>
                </div>
        );

};

export default SingleProduct;


