// Path: frontend/src/admin/pages/products/CategoryManagement.jsx
import { useState } from 'react';
import { FiPlus, FiEdit2, FiTrash2 } from 'react-icons/fi';

export default function CategoryManagement() {
  // Mock data - replace with API calls
  const [categories, setCategories] = useState([
    { id: 'cat_1', name: 'Rings', description: 'Various ring collections' },
    { id: 'cat_2', name: 'Necklaces', description: 'Necklace designs' },
    { id: 'cat_3', name: 'Bracelets', description: 'Handcrafted bracelets' }
  ]);

  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });

  const [editingId, setEditingId] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      if (editingId) {
        // Update existing category
        setCategories(categories.map(cat => 
          cat.id === editingId ? { ...formData, id: editingId } : cat
        ));
      } else {
        // Add new category
        const newCategory = {
          ...formData,
          id: `cat_${categories.length + 1}`
        };
        setCategories([...categories, newCategory]);
      }

      setFormData({ name: '', description: '' });
      setEditingId(null);
      setIsSubmitting(false);
    }, 500);
  };

  const handleEdit = (category) => {
    setFormData({
      name: category.name,
      description: category.description
    });
    setEditingId(category.id);
  };

  const handleDelete = (categoryId) => {
    if (window.confirm('Are you sure you want to delete this category?')) {
      setCategories(categories.filter(cat => cat.id !== categoryId));
    }
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-2xl font-serif font-bold text-rose-800">Category Management</h1>
        <p className="text-amber-900/80">Organize your jewelry collections</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Add/Edit Form */}
        <div className="bg-white rounded-xl shadow-sm border border-rose-100 p-6 h-fit">
          <h2 className="text-lg font-semibold text-amber-900 mb-4">
            {editingId ? 'Edit Category' : 'Add New Category'}
          </h2>
          
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-amber-900 text-sm font-medium mb-1">
                Category Name
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                className="w-full px-4 py-2 rounded-lg border border-rose-200 focus:ring-2 focus:ring-rose-300 focus:border-transparent"
                placeholder="e.g., Wedding Rings"
                required
              />
            </div>

            <div className="mb-6">
              <label className="block text-amber-900 text-sm font-medium mb-1">
                Description
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                rows={3}
                className="w-full px-4 py-2 rounded-lg border border-rose-200 focus:ring-2 focus:ring-rose-300 focus:border-transparent"
                placeholder="Brief description of this category"
              />
            </div>

            <div className="flex justify-end gap-3">
              {editingId && (
                <button
                  type="button"
                  onClick={() => {
                    setFormData({ name: '', description: '' });
                    setEditingId(null);
                  }}
                  className="px-4 py-2 border border-rose-200 text-amber-900 rounded-lg hover:bg-rose-50"
                >
                  Cancel Edit
                </button>
              )}
              <button
                type="submit"
                className="px-4 py-2 bg-rose-700 text-white rounded-lg hover:bg-rose-800 flex items-center gap-2"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  'Processing...'
                ) : (
                  <>
                    <FiPlus />
                    {editingId ? 'Update Category' : 'Add Category'}
                  </>
                )}
              </button>
            </div>
          </form>
        </div>

        {/* Categories Table */}
        <div className="bg-white rounded-xl shadow-sm border border-rose-100 overflow-hidden">
          <table className="min-w-full divide-y divide-rose-100">
            <thead className="bg-rose-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-amber-900 uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-amber-900 uppercase tracking-wider">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-amber-900 uppercase tracking-wider">Description</th>
                <th className="px-6 py-3 text-right text-xs font-medium text-amber-900 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-rose-50">
              {categories.map((category) => (
                <tr key={category.id} className="hover:bg-rose-50/50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-rose-800">{category.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-amber-900">{category.name}</td>
                  <td className="px-6 py-4 text-sm text-amber-900 max-w-xs truncate">{category.description}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-2">
                    <button
                      onClick={() => handleEdit(category)}
                      className="text-amber-600 hover:text-amber-800"
                    >
                      <FiEdit2 />
                    </button>
                    <button
                      onClick={() => handleDelete(category.id)}
                      className="text-rose-600 hover:text-rose-800"
                    >
                      <FiTrash2 />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
