import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { User, Mail, Phone, MapPin, Camera, Loader2 } from 'lucide-react';

const EditProfile = () => {
        const [formData, setFormData] = useState({ name: "", email: "", phone: "", address: "" });
        const [message, setMessage] = useState("");
        const [loading, setLoading] = useState(false);

        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setFormData({ name: data.name, email: data.email, phone: data.phone, address: data.address });
                        } catch (error) {
                                setMessage("❌ Failed to fetch user profile.");
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleChange = (e) => {
                setFormData({ ...formData, [e.target.name]: e.target.value });
        };

        const handleSubmit = async (e) => {
                e.preventDefault();
                try {
                        const token = localStorage.getItem("userToken");

                        await axios.put("http://localhost:5000/api/users/profile", formData, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setMessage("✅ Profile updated successfully!");

                        setTimeout(() => {
                                setMessage("");
                                navigate("/profile");
                        }, 1500);
                } catch (error) {
                        setMessage("❌ Failed to update profile.");
                }

                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };

        return (
                <div className="p-12 flex flex-col justify-center">
                        <div className="space-y-8">
                                {/* Header Section */}
                                <div className="text-center lg:text-left">
                                        <h2 className="text-4xl font-serif text-[#6B4E4D] mb-2">Update Profile</h2>
                                        <p className="text-[#6B4E4D]/80">Manage your luxury account details</p>
                                </div>

                                {/* Profile Picture Section */}
                                <div className="flex flex-col items-center space-y-4">
                                        <div className="relative group">
                                                <img
                                                        src={formData.profileImage}
                                                        className="w-32 h-32 rounded-full border-2 border-[#F5EDE1] hover:border-[#B76E79]/40 transition-colors"
                                                        alt="Profile"
                                                />
                                                <button
                                                        type="button"
                                                        onClick={() => {/* Add image upload handler */ }}
                                                        className="absolute bottom-2 right-2 bg-gradient-to-br from-[#B76E79] to-[#C5A880] p-2 rounded-full text-white shadow-lg hover:scale-105 transition-transform"
                                                >
                                                        <Camera className="w-6 h-6" />
                                                </button>
                                        </div>
                                        {profileError && <p className="text-[#B76E79] text-sm">{profileError}</p>}
                                </div>

                                {/* Form Fields */}
                                <form onSubmit={handleSubmit} className="space-y-6">
                                        <div className="space-y-4">
                                                {/* Full Name Field */}
                                                <div className="relative">
                                                        <input
                                                                type="text"
                                                                name="name"
                                                                value={formData.name}
                                                                onChange={handleChange}
                                                                className="w-full px-6 py-4 bg-[#FAF6F0] border-2 border-[#F5EDE1] rounded-xl focus:border-[#B76E79]/40 focus:ring-0 placeholder-[#C5A880] text-[#6B4E4D]"
                                                                placeholder="Full Name"
                                                                required
                                                        />
                                                        <User className="absolute right-6 top-1/2 -translate-y-1/2 text-[#C5A880]" />
                                                </div>

                                                {/* Email Field */}
                                                <div className="relative">
                                                        <input
                                                                type="email"
                                                                name="email"
                                                                value={formData.email}
                                                                onChange={handleChange}
                                                                className="w-full px-6 py-4 bg-[#FAF6F0] border-2 border-[#F5EDE1] rounded-xl focus:border-[#B76E79]/40 focus:ring-0 placeholder-[#C5A880] text-[#6B4E4D]"
                                                                placeholder="Email Address"
                                                                required
                                                        />
                                                        <Mail className="absolute right-6 top-1/2 -translate-y-1/2 text-[#C5A880]" />
                                                </div>

                                                {/* Phone Field */}
                                                <div className="relative">
                                                        <input
                                                                type="tel"
                                                                name="phone"
                                                                value={formData.phone}
                                                                onChange={handleChange}
                                                                className="w-full px-6 py-4 bg-[#FAF6F0] border-2 border-[#F5EDE1] rounded-xl focus:border-[#B76E79]/40 focus:ring-0 placeholder-[#C5A880] text-[#6B4E4D]"
                                                                placeholder="Phone Number"
                                                                required
                                                        />
                                                        <Phone className="absolute right-6 top-1/2 -translate-y-1/2 text-[#C5A880]" />
                                                </div>

                                                {/* Address Field */}
                                                <div className="relative">
                                                        <input
                                                                type="text"
                                                                name="address"
                                                                value={formData.address}
                                                                onChange={handleChange}
                                                                className="w-full px-6 py-4 bg-[#FAF6F0] border-2 border-[#F5EDE1] rounded-xl focus:border-[#B76E79]/40 focus:ring-0 placeholder-[#C5A880] text-[#6B4E4D]"
                                                                placeholder="Shipping Address"
                                                                required
                                                        />
                                                        <MapPin className="absolute right-6 top-1/2 -translate-y-1/2 text-[#C5A880]" />
                                                </div>
                                        </div>

                                        {/* Action Section */}
                                        <div className="flex justify-between items-center">
                                                <Link
                                                        to="/profile"
                                                        className="text-[#6B4E4D]/80 hover:text-[#B76E79] transition-colors text-sm"
                                                >
                                                        Cancel Changes
                                                </Link>
                                                <button
                                                        type="submit"
                                                        className="px-8 py-4 bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white rounded-xl font-semibold hover:shadow-lg transition-all"
                                                        disabled={loading}
                                                >
                                                        {loading ? (
                                                                <div className="flex items-center justify-center gap-2">
                                                                        <Loader2 className="w-5 h-5 animate-spin" />
                                                                        Saving...
                                                                </div>
                                                        ) : "Update Profile"}
                                                </button>
                                        </div>
                                </form>

                                {/* Security Assurance */}
                                <div className="pt-6 border-t border-[#F5EDE1]">
                                        <p className="text-center text-[#6B4E4D]/80 text-sm">
                                                Secured with 256-bit encryption • All changes are logged
                                        </p>
                                </div>
                        </div>
                </div>
        );
};

export default EditProfile;
