import React, { useState } from "react";

const Cart = () => {
        // Sample Cart Items
        

        // Function to update quantity
        const updateQuantity = (id, action) => {
                setCartItems((prevItems) =>
                        prevItems.map((item) =>
                                item.id === id
                                        ? { ...item, quantity: action === "increase" ? item.quantity + 1 : Math.max(1, item.quantity - 1) }
                                        : item
                        )
                );
        };

        // Function to remove item
        const removeItem = (id) => {
                setCartItems(cartItems.filter((item) => item.id !== id));
        };

        // Calculate total price
        const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
        const tax = subtotal * 0.1;
        const total = subtotal + tax;

        return (
                <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
                        <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-lg flex flex-col md:flex-row gap-6">

                                {/* Left Side: Cart Items List */}
                                <div className="w-full md:w-2/3 bg-gray-50 p-6 rounded-lg shadow-md">
                                        <h1 className="text-3xl font-semibold text-gray-800 mb-6">Your Shopping Cart</h1>
                                        {cartItems.length > 0 ? (
                                                <div className="space-y-4">
                                                        {cartItems.map((item) => (
                                                                <div key={item.id} className="flex items-center justify-between bg-white p-4 rounded-lg shadow">
                                                                        <div className="flex items-center space-x-4">
                                                                                <img src={item.image} alt={item.name} className="w-20 h-20 object-cover rounded-md" />
                                                                                <div>
                                                                                        <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                                                                                        <p className="text-gray-600">${item.price.toFixed(2)}</p>
                                                                                </div>
                                                                        </div>

                                                                        {/* Quantity Selector */}
                                                                        <div className="flex items-center space-x-2">
                                                                                <button
                                                                                        onClick={() => updateQuantity(item.id, "decrease")}
                                                                                        className="px-3 py-1 bg-gray-300 rounded hover:bg-gray-400"
                                                                                >
                                                                                        −
                                                                                </button>
                                                                                <span className="px-3 py-1 border rounded">{item.quantity}</span>
                                                                                <button
                                                                                        onClick={() => updateQuantity(item.id, "increase")}
                                                                                        className="px-3 py-1 bg-gray-300 rounded hover:bg-gray-400"
                                                                                >
                                                                                        +
                                                                                </button>
                                                                        </div>

                                                                        {/* Remove Button */}
                                                                        <button
                                                                                onClick={() => removeItem(item.id)}
                                                                                className="text-red-500 hover:text-red-700 transition font-medium"
                                                                        >
                                                                                Remove
                                                                        </button>
                                                                </div>
                                                        ))}
                                                </div>
                                        ) : (
                                                <div className="text-center py-12">
                                                        <p className="text-lg text-gray-500">Your cart is empty.</p>
                                                        <button className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition">
                                                                Continue Shopping
                                                        </button>
                                                </div>
                                        )}
                                </div>

                                {/* Right Side: Cart Summary */}
                                <div className="w-full md:w-1/3 bg-gray-50 p-6 rounded-lg shadow-md">
                                        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Cart Summary</h2>
                                        <div className="space-y-2 text-gray-600">
                                                <p>Subtotal: <span className="float-right">${subtotal.toFixed(2)}</span></p>
                                                <p>Tax (10%): <span className="float-right">${tax.toFixed(2)}</span></p>
                                                <hr />
                                                <p className="text-lg font-semibold">Total: <span className="float-right text-gray-900">${total.toFixed(2)}</span></p>
                                        </div>

                                        {/* Checkout Buttons */}
                                        <div className="mt-6 flex flex-col space-y-3">
                                                <button className="bg-gray-300 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-400 transition">
                                                        Continue Shopping
                                                </button>
                                                <button className="bg-amber-600 text-white px-6 py-2 rounded-lg shadow hover:bg-amber-700 transition">
                                                        Proceed to Checkout
                                                </button>
                                        </div>
                                </div>

                        </div>
                </div>
        );
};

export default Cart;
