import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { MapPin, Phone, Edit, LogOut } from "lucide-react";


const Profile = () => {
        // profile
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };

                const fetchReviewsByUser = async () => {
                        try {

                        } catch(error) {
                                console.log("ctch block of fetchReviewByUser");
                        }
                };


                fetchReviewsByUser();
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;
        if (error) return <div className="text-center text-red-500">{error}</div>;

        return (
                <>
                        <div className="flex justify-center items-center min-h-screen bg-[#f7f3ee] px-6">

                                {/* Floating Decorative Elements */}
                                <div className="absolute top-10 left-10 w-24 h-24 bg-[#B88E8D] rounded-full opacity-30"></div>
                                <div className="absolute bottom-16 right-16 w-32 h-32 bg-[#D8B6A4] rounded-full opacity-20"></div>

                                {/* Profile Container */}
                                <div className="w-full max-w-4xl bg-white/80 shadow-2xl rounded-2xl backdrop-blur-lg border border-[#C69C84] p-8 flex space-x-10">

                                        {/* Left Side: Profile Picture Only */}
                                        <div className="w-1/4 flex justify-center items-start border-r-2 ">
                                                <img
                                                        className="w-32 h-32 rounded-full border-4 border-[#D4A190] shadow-lg"
                                                        src={`https://ui-avatars.com/api/?name=${user?.name}&background=9B6A62&color=fff`}
                                                        alt="User Avatar"
                                                />
                                        </div>

                                        {/* Right Side: Profile Details & Actions */}
                                        <div className="flex-1 space-y-6">

                                                {/* Name & Email (Upper Section) */}
                                                <div className="text-center flex justify-start items-end gap-3">
                                                        <h1 className="text-4xl font-semibold text-[#9B6A62]">{user?.name}</h1>
                                                        <p className="text-gray-600 text-lg">{user?.email}</p>
                                                </div>

                                                {/* Contact Details (Middle Section) */}
                                                <div className="bg-white/60 rounded-lg p-6 shadow-md border border-[#D4A190] space-y-4">
                                                        <div className="flex items-center gap-4 text-gray-700">
                                                                <Phone className="text-[#9B6A62] w-6 h-6" />
                                                                <p className="text-lg"><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                                        </div>
                                                        <div className="flex items-center gap-4 text-gray-700">
                                                                <MapPin className="text-[#9B6A62] w-6 h-6" />
                                                                <p className="text-lg"><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                                        </div>
                                                </div>

                                                {/* Action Buttons (Bottom Section) */}
                                                <div className="flex justify-between space-x-6">
                                                        <button
                                                                onClick={() => navigate("/edit-profile")}
                                                                className="flex items-center justify-center gap-3 w-1/2 bg-gradient-to-r from-[#D4A190] to-[#9B6A62] text-white py-3 px-6 rounded-xl hover:scale-95 transition-all shadow-lg"
                                                        >
                                                                <Edit className="w-6 h-6" /> Edit Profile
                                                        </button>
                                                        <button
                                                                onClick={handleLogout}
                                                                className="flex items-center justify-center gap-3 w-1/2 bg-gradient-to-r from-[#E87768] to-[#C04848] text-white py-3 px-6 rounded-xl hover:scale-95 transition-all shadow-lg"
                                                        >
                                                                <LogOut className="w-6 h-6" /> Logout
                                                        </button>
                                                </div>

                                        </div>

                                </div>

                        </div>

                        <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-lg mt-10">
                                <h2 className="text-2xl font-bold text-gray-800 mb-4">Customer Reviews</h2>

                                {reviews.length > 0 ? (
                                        reviews.map((review) => (
                                                <div key={review._id} className="border-b border-gray-200 py-4">
                                                        <div className="flex items-center gap-4">
                                                                <div className="bg-blue-500 text-white w-10 h-10 flex items-center justify-center rounded-full font-bold">
                                                                        {review.user?.name?.charAt(0) || "U"}
                                                                </div>
                                                                <div>
                                                                        <h3 className="font-semibold text-gray-800">{review.user?.name || "Anonymous"}</h3>
                                                                        <p className="text-sm text-gray-500">{new Date(review.createdAt).toLocaleDateString()}</p>
                                                                </div>
                                                        </div>

                                                        <div className="mt-2">
                                                                <div className="flex text-yellow-500">
                                                                        {Array.from({ length: 5 }).map((_, index) => (
                                                                                <svg
                                                                                        key={index}
                                                                                        xmlns="http://www.w3.org/2000/svg"
                                                                                        fill={index < review.rating ? "currentColor" : "none"}
                                                                                        viewBox="0 0 24 24"
                                                                                        stroke="currentColor"
                                                                                        className="w-5 h-5"
                                                                                >
                                                                                        <path
                                                                                                strokeLinecap="round"
                                                                                                strokeLinejoin="round"
                                                                                                strokeWidth="2"
                                                                                                d="M12 4.5l2.8 5.7 6.3.9-4.5 4.4 1.1 6.3-5.7-3-5.7 3 1.1-6.3-4.5-4.4 6.3-.9L12 4.5z"
                                                                                        />
                                                                                </svg>
                                                                        ))}
                                                                </div>
                                                                <p className="mt-2 text-gray-700">{review.comment}</p>
                                                        </div>
                                                </div>
                                        ))
                                ) : (
                                        <p className="text-gray-500">You haven't reviewed any products. Purchase something.!</p>
                                )}
                        </div>

                </>

        );
};

export default Profile;
