// Cart.js - Fully Functional React Component
import React, { useState, useEffect } from "react";

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);
    const userId = localStorage.getItem("userId");
    const token = localStorage.getItem("userToken");

    const fetchCart = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                }
            });
            if (!res.ok) throw new Error("Failed to fetch cart");
            const data = await res.json();
            setCartItems(data.products || []);
        } catch (err) {
            console.error("Error fetching cart:", err);
        }
    };

    useEffect(() => {
        if (userId && token) {
            fetchCart();
        }
    }, [userId, token]);

    const updateCart = async (productId, action) => {
        try {
            const res = await fetch("http://localhost:5000/api/cart/update", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify({ userId, productId, action })
            });
            if (!res.ok) throw new Error("Failed to update cart");
            fetchCart(); // Refresh cart after update
        } catch (err) {
            console.error("Error updating cart:", err);
        }
    };

    const removeItem = async (productId) => {
        try {
            const res = await fetch("http://localhost:5000/api/cart/remove", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify({ userId, productId })
            });
            if (!res.ok) throw new Error("Failed to remove item");
            fetchCart(); // Refresh cart after removing item
        } catch (err) {
            console.error("Error removing item:", err);
        }
    };

    return (
        <div>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                cartItems.map(item => (
                    <div key={item.product._id}>
                        <h3>{item.product.name} - ${item.product.price}</h3>
                        <button onClick={() => updateCart(item.product._id, "decrease")} disabled={item.quantity <= 1}>-</button>
                        <span>{item.quantity}</span>
                        <button onClick={() => updateCart(item.product._id, "increase")}>+</button>
                        <button onClick={() => removeItem(item.product._id)}>Remove</button>
                    </div>
                ))
            )}
        </div>
    );
};

export default Cart;
