import { useState, useEffect } from "react";

const CheckoutPage = ({ cartItems, userAddress }) => {
  const [address, setAddress] = useState(userAddress || ""); // Load user address

  // Calculate total price
  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const tax = subtotal * 0.1; // 10% Tax
  const shippingCharge = 10; // Fixed ₹10 shipping fee
  const totalAmount = subtotal + tax + shippingCharge; // Final amount

  useEffect(() => {
    // Example: Fetch user's saved address dynamically from an API
    setAddress(userAddress || "123 Main St, City, Country");
  }, [userAddress]);

  const handleProceed = () => {
    alert(`Proceeding to payment for ₹${totalAmount.toFixed(2)}`);
    // Add your payment API call here
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10 border border-gray-200">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Checkout</h2>

      {/* Order Summary */}
      <div className="border p-4 rounded-lg mb-6 bg-gray-50">
        <h3 className="text-lg font-semibold mb-2 text-gray-700">Order Summary</h3>
        {cartItems.map((item) => (
          <div key={item.id} className="flex justify-between border-b py-2 text-gray-600">
            <span>{item.name} (x{item.quantity})</span>
            <span className="font-medium">₹{(item.price * item.quantity).toFixed(2)}</span>
          </div>
        ))}
        <div className="flex justify-between mt-2 text-gray-800">
          <span>Subtotal:</span>
          <span>₹{subtotal.toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-gray-800">
          <span>Tax (10%):</span>
          <span>₹{tax.toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-gray-800">
          <span>Shipping:</span>
          <span>₹{shippingCharge.toFixed(2)}</span>
        </div>
        <div className="flex justify-between font-semibold mt-2 text-gray-900">
          <span>Total:</span>
          <span>₹{totalAmount.toFixed(2)}</span>
        </div>
      </div>

      {/* Address Section */}
      <div className="border p-4 rounded-lg mb-6 bg-gray-50">
        <h3 className="text-lg font-semibold mb-2 text-gray-700">Shipping Address</h3>
        <textarea 
          className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none"
          value={address} 
          onChange={(e) => setAddress(e.target.value)}
        />
      </div>

      {/* Proceed to Payment */}
      <button 
        onClick={handleProceed} 
        className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition font-semibold">
        Proceed to Payment (₹{totalAmount.toFixed(2)})
      </button>
    </div>
  );
};

// Example default props for testing
CheckoutPage.defaultProps = {
  cartItems: [
    { id: 1, name: "Product A", quantity: 2, price: 20 },
    { id: 2, name: "Product B", quantity: 1, price: 35 },
  ],
  userAddress: "456 Park Ave, City, Country"
};

export default CheckoutPage;
