import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

const CheckoutPage = () => {
        const location = useLocation();
        const navigate = useNavigate();

        const cartItems = location.state?.cartItems || [];
        const subtotal = location.state?.total || 0;
        const finalAmount = location.state?.finalAmount || subtotal; // Use final amount directly

        const [address, setAddress] = useState("");
        const [addressError, setAddressError] = useState(""); // New state for address validation
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const [user, setUser] = useState(null);

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                                setAddress(data.address || "");
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        // Address validation function
        const validateAddress = (input) => {
                // Blocked words (you can add more)
                const blockedWords = ["fake", "test", "unknown", "n/a", "123", "####"];

                // Regex to allow letters, numbers, spaces, commas, and common symbols
                const validAddressPattern = /^[a-zA-Z0-9\s,.-]+$/;

                if (input.length < 10) {
                        setAddressError("Address must be at least 10 characters long.");
                        return false;
                }

                if (!validAddressPattern.test(input)) {
                        setAddressError("Address contains invalid characters.");
                        return false;
                }

                if (blockedWords.some((word) => input.toLowerCase().includes(word))) {
                        setAddressError("This address is not valid for shipping.");
                        return false;
                }

                setAddressError(""); // Clear error if valid
                return true;
        };

        const handleAddressChange = (e) => {
                const newAddress = e.target.value;
                setAddress(newAddress);
                validateAddress(newAddress); // Validate on change
        };


        const handlePayment = async (cartItems, totalAmount, user) => {
                try {
                    console.log("✅ Debug - Inside handlePayment");
                    console.log("✅ Received Cart Items:", cartItems);
                    console.log("✅ Received Total Amount:", totalAmount);
                    console.log("✅ Received User:", user);
            
                    // ✅ Validate user before proceeding
                    if (!user?.id) {
                        console.error("❌ User is missing or invalid! Make sure you are logged in.");
                        alert("❌ User data is missing. Please log in again.");
                        return;
                    }
            
                    // ✅ Extract user ID and convert to string (in case it's a number)
                    const userId = String(user.id);
                    console.log("✅ Extracted User ID:", userId);
            
                    // ✅ Validate Cart Items
                    if (!cartItems || cartItems.length === 0) {
                        console.error("❌ Cart is empty. Cannot proceed to payment.");
                        alert("❌ Your cart is empty! Add items before proceeding.");
                        return;
                    }
            
                    // ✅ Prepare payment data
                    const paymentData = {
                        user: userId, // Ensure user ID is passed correctly
                        items: cartItems.map(item => ({
                            productId: item.productId,
                            name: item.name,
                            quantity: item.quantity,
                            price: item.price
                        })),
                        totalAmount: totalAmount,
                        paymentId: "N/A",
                        paymentStatus: "Unpaid"
                    };
            
                    console.log("✅ Final Payment Data:", paymentData);
            
                    // ✅ Fetch Auth Token (if required)
                    const authToken = localStorage.getItem("authToken");
                    if (!authToken) {
                        console.error("❌ Authentication Token Missing!");
                        alert("❌ Please log in again to proceed.");
                        return;
                    }
            
                    // ✅ Make API Call to Process Payment
                    const response = await fetch("/api/payment", {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: `Bearer ${authToken}`
                        },
                        body: JSON.stringify(paymentData)
                    });
            
                    const result = await response.json();
                    console.log("✅ Payment API Response:", result);
            
                    if (response.ok) {
                        alert("✅ Payment processed successfully!");
                    } else {
                        console.error("❌ Payment Failed:", result.message);
                        alert(`❌ Payment failed: ${result.message}`);
                    }
                } catch (error) {
                    console.error("❌ Error in handlePayment:", error);
                    alert("❌ An error occurred while processing your payment. Please try again.");
                }
            };
            

        return (
                <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10 border border-gray-200">
                        <h2 className="text-2xl font-bold mb-4 text-gray-800">Checkout</h2>

                        <div className="border p-4 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-lg font-semibold mb-2 text-gray-700">Order Summary</h3>
                                {cartItems.map((item) => (
                                        <div key={item.id} className="flex justify-between border-b py-2 text-gray-600">
                                                <span>{item.name} (x{item.quantity})</span>
                                                <span className="font-medium">₹{(item.price * item.quantity).toFixed(2)}</span>
                                        </div>
                                ))}
                                <div className="flex justify-between mt-2 text-gray-800">
                                        <span>Subtotal:</span>
                                        <span>₹{subtotal.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between text-gray-800">
                                        <span>Tax & Shipping:</span>
                                        <span>Included</span>
                                </div>
                                <div className="flex justify-between font-semibold mt-2 text-gray-900">
                                        <span>Final Payable Amount:</span>
                                        <span>₹{finalAmount.toFixed(2)}</span>
                                </div>
                        </div>

                        <div className="border p-4 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-lg font-semibold mb-2 text-gray-700">Shipping Address</h3>
                                <textarea
                                        className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                        value={address}
                                        onChange={handleAddressChange}
                                        placeholder="Enter shipping address..."
                                />
                                {addressError && <p className="text-red-500 mt-1">{addressError}</p>}
                        </div>

                        <button
                                onClick={() => handlePayment(cartItems, finalAmount, user)} className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition font-semibold disabled:opacity-50"
                                disabled={cartItems.length === 0}
                        >
                                {cartItems.length > 0 ? `Proceed to Payment (₹${finalAmount.toFixed(2)})` : "Cart is Empty"}
                        </button>
                </div>
        );
};

export default CheckoutPage;
