import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ShoppingCart, Trash2, Minus, Plus, CreditCard } from "lucide-react";


const Cart = () => {
        const [cartItems, setCartItems] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const navigate = useNavigate();
        const userId = localStorage.getItem("userId");
        const token = localStorage.getItem("userToken");

        const fetchCart = async () => {
                try {
                        const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                                method: "GET",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                        });

                        const data = await response.json();

                        if (response.ok) {
                                setCartItems(data.products || []);
                        } else {
                                setError(data.message || "Failed to load cart.");
                        }
                } catch (err) {
                        console.error("Fetch error:", err);
                        setError("Server error while fetching cart.");
                } finally {
                        setLoading(false);
                }
        };

        useEffect(() => {
                if (userId && token) {
                        fetchCart();
                }
        }, [userId, token]);

        const updateQuantity = async (id, amount) => {
                const newQuantity = cartItems.find((item) => item.product._id === id)?.quantity + amount;
                if (newQuantity < 1) return removeItem(id);

                try {
                        await fetch("http://localhost:5000/api/cart/update", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id, quantity: newQuantity }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Update error:", err);
                }
        };

        const removeItem = async (id) => {
                try {
                        await fetch("http://localhost:5000/api/cart/remove", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Remove error:", err);
                }
        };

        const handleCheckout = () => {
                navigate("/checkout", {
                        state: {
                                cartItems,
                                subtotal,
                                tax,
                                shipping,
                                total
                        }
                });
        };


        const subtotal = cartItems.reduce((acc, item) => acc + item.product.price * item.quantity, 0);
        const tax = subtotal * 0.1;
        const shipping = subtotal > 0 ? 10 : 0;
        const total = subtotal + tax + shipping;

        if (loading) return <div>Loading...</div>;
        if (error) return <div className="text-red-600">{error}</div>;

        return (
                <div className="max-w-6xl mx-auto p-6 mt-[100px] grid grid-cols-1 lg:grid-cols-3 gap-8">
                        {/* Shopping Cart Section */}
                        <div className="lg:col-span-2 bg-white shadow-2xl rounded-xl p-6">
                                <div className="flex items-center gap-3 mb-6 text-[#6B4E4D]">
                                        <ShoppingCart className="w-7 h-7" />
                                        <h2 className="text-3xl font-bold">Shopping Cart</h2>
                                </div>

                                {cartItems.length === 0 ? (
                                        <p className="text-gray-500 text-lg">Your cart is empty.</p>
                                ) : (
                                        <div className="space-y-6">
                                                {cartItems.map((item) => (
                                                        <div key={item.product._id} className="flex justify-between items-center p-5 bg-[#F9F6F3] rounded-lg shadow-md">
                                                                {/* Product Image & Details */}
                                                                <div className="flex items-center gap-5">
                                                                        <img src={item.product.image} alt={item.product.name} className="w-20 h-20 object-cover rounded-lg border border-[#D8B6A4]" />
                                                                        <div>
                                                                                <h3 className="text-lg font-semibold text-[#6B4E4D]">{item.product.name}</h3>
                                                                                <p className="text-gray-600 text-sm">${item.product.price.toFixed(2)}</p>
                                                                        </div>
                                                                </div>

                                                                {/* Quantity Control */}
                                                                <div className="flex items-center gap-3 bg-[#F3E5DC] px-4 py-2 rounded-lg">
                                                                        <button
                                                                                onClick={() => updateQuantity(item.product._id, -1)}
                                                                                className="text-[#6B4E4D] hover:bg-[#D8B6A4] p-2 rounded transition"
                                                                        >
                                                                                <Minus className="w-4 h-4" />
                                                                        </button>
                                                                        <span className="text-lg font-semibold">{item.quantity}</span>
                                                                        <button
                                                                                onClick={() => updateQuantity(item.product._id, 1)}
                                                                                className="text-[#6B4E4D] hover:bg-[#D8B6A4] p-2 rounded transition"
                                                                        >
                                                                                <Plus className="w-4 h-4" />
                                                                        </button>
                                                                </div>

                                                                {/* Price & Remove Button */}
                                                                <div className="text-center">
                                                                        <p className="text-lg font-semibold text-[#6B4E4D]">${(item.product.price * item.quantity).toFixed(2)}</p>
                                                                        <button onClick={() => removeItem(item.product._id)} className="mt-2 text-sm text-red-500 hover:text-red-700 flex items-center gap-1">
                                                                                <Trash2 className="w-4 h-4" />
                                                                                Remove
                                                                        </button>
                                                                </div>
                                                        </div>
                                                ))}
                                        </div>
                                )}
                        </div>

                        {/* Cart Summary Section */}
                        <div className="p-6 bg-[#F9ECE3] rounded-xl shadow-xl h-fit">
                                <div className="flex items-center gap-3 mb-4 text-[#6B4E4D]">
                                        <CreditCard className="w-8 h-8" />
                                        <h3 className="text-2xl font-semibold">Order Summary</h3>
                                </div>
                                <div className="space-y-3">
                                        <div className="flex justify-between"><span>Subtotal:</span><span className="font-semibold">${subtotal.toFixed(2)}</span></div>
                                        <div className="flex justify-between"><span>Making Charges(10%):</span><span className="font-semibold">${tax.toFixed(2)}</span></div>
                                        <div className="flex justify-between"><span>Shipping:</span><span className="font-semibold">${shipping.toFixed(2)}</span></div>
                                </div>
                                <hr className="my-4 border-[#D8B6A4]" />
                                <div className="flex justify-between text-xl font-bold text-[#6B4E4D]">
                                        <span>Total:</span><span>${total.toFixed(2)}</span>
                                </div>

                                {/* Checkout Button */}
                                <button
                                        className={`w-full mt-6 py-3 rounded-lg font-semibold text-lg transition duration-300 ${total === 0 ? "bg-gray-400 cursor-not-allowed" : "bg-[#B88E8D] hover:bg-[#A67D7C] text-white"
                                                }`}
                                        disabled={total === 0}
                                        onClick={handleCheckout}
                                >
                                        Proceed to Checkout
                                </button>
                        </div>
                </div>
        );
};

export default Cart;
