import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link from React Router
import axios from "axios";

const ProductListing = () => {
        const [products, setProducts] = useState([]);
        const [categories, setCategories] = useState([]); // Fetch categories dynamically
        const [searchQuery, setSearchQuery] = useState("");
        const [selectedCategories, setSelectedCategories] = useState([]);
        const [priceRange, setPriceRange] = useState(20000);
        const [message, setMessage] = useState(""); // State for feedback messages


        useEffect(() => {
                const fetchProducts = async () => {
                        try {
                                const response = await axios.get("http://localhost:5000/api/products");
                                setProducts(response.data);
                        } catch (error) {
                                console.error("Error fetching products:", error);
                        }
                };

                const fetchCategories = async () => {
                        try {
                                const response = await axios.get("http://localhost:5000/api/categories");
                                setCategories(response.data); // Assume backend returns an array of category names
                        } catch (error) {
                                console.error("Error fetching categories:", error);
                        }
                };

                fetchProducts();
                fetchCategories();
        }, []);

        const handleCategoryChange = (category) => {
                setSelectedCategories((prevCategories) =>
                        prevCategories.includes(category)
                                ? prevCategories.filter((c) => c !== category)
                                : [...prevCategories, category]
                );
        };

        const handleAddToCart = async (id) => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        return;
                }

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();

                        if (response.ok) {
                                setMessage("✅ Item added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                // Hide message after 3 seconds
                setTimeout(() => setMessage(""), 3000);
        };




        const filteredProducts = products.filter((product) => {
                const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
                const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(product.category);
                const matchesPrice = product.price <= priceRange;
                return matchesSearch && matchesCategory && matchesPrice;
        });

        return (
                <div className="min-h-screen bg-gray-50 flex">
                        <aside className="w-1/4 bg-white p-8 shadow-2xl rounded-2xl border border-gray-200 backdrop-blur-lg">
                                <p className="mt-5"></p>
                                <h2 className="text-3xl font-extrabold mb-6 text-[#b76e79] tracking-wide">Filters</h2>

                                {/* Search Bar */}
                                <div className="mb-6">
                                        <input
                                                type="text"
                                                placeholder="Search products..."
                                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#b76e79] transition duration-200 shadow-sm text-gray-800 placeholder-gray-500"
                                                value={searchQuery}
                                                onChange={(e) => setSearchQuery(e.target.value)}
                                        />
                                </div>

                                {/* Categories */}
                                <div className="mb-6">
                                        <h3 className="text-xl font-semibold text-gray-700 mb-4">Categories</h3>
                                        <ul className="space-y-3">
                                                {categories.length > 0 ? (
                                                        categories.map((category) => (
                                                                <li key={category._id} className="flex items-center space-x-3">
                                                                        <input
                                                                                type="checkbox"
                                                                                id={category.name}
                                                                                className="w-5 h-5 text-[#b76e79] border-gray-300 rounded focus:ring-[#b76e79] cursor-pointer"
                                                                                checked={selectedCategories.includes(category.name)}
                                                                                onChange={() => handleCategoryChange(category.name)}
                                                                        />
                                                                        <label htmlFor={category.name} className="text-gray-800 cursor-pointer hover:text-[#b76e79] transition">
                                                                                {category.name}
                                                                        </label>
                                                                </li>
                                                        ))
                                                ) : (
                                                        <p className="text-gray-500">Loading categories...</p>
                                                )}
                                        </ul>
                                </div>

                                {/* Price Range */}
                                <div className="mb-6">
                                        <h3 className="text-xl font-semibold text-gray-700 mb-4">
                                                Max Price Range: <span className="text-[#b76e79] font-bold">${priceRange}</span>
                                        </h3>
                                        <input
                                                type="range"
                                                className="w-full cursor-pointer appearance-none h-2 bg-gray-300 rounded-lg outline-none accent-[#b76e79]"
                                                min="0"
                                                max="20000"
                                                value={priceRange}
                                                onChange={(e) => setPriceRange(e.target.value)}
                                        />
                                </div>
                        </aside>



                        <main className="w-3/4 p-6">
                                {message && (
                                        <div className="mb-4 p-3 text-white text-center bg-green-500 rounded-md">
                                                {message}
                                        </div>
                                )}

                                <h1 className="text-3xl font-bold mb-6">Our Products</h1>
                                <div className="grid grid-cols-3 gap-6">
                                        {filteredProducts.length > 0 ? (
                                                filteredProducts.map((product) => (

                                                        <div key={product._id} className="relative bg-white rounded-xl shadow-xl border border-gray-200 overflow-visible group transition-all duration-300 hover:shadow-2xl">
                                                                {/* Product Image (Floating Effect on Hover) */}
                                                                <div className="relative overflow-visible">
                                                                        <img
                                                                                src={product.images[0]} alt={product.name}
                                                                                className="w-full h-64 object-cover transition-all duration-500 group-hover:-translate-y-8 group-hover:z-10"
                                                                        />

                                                                        {/* Floating Effect Shadow */}
                                                                        <div className="absolute bottom-0 left-0 right-0 h-10 bg-gradient-to-t from-gray-200 to-transparent opacity-100 group-hover:opacity-0 transition-all duration-500"></div>

                                                                        {/* Slide-Up Buttons */}
                                                                        <div className="absolute bottom-0 left-0 right-0 flex items-center justify-center space-x-4 opacity-0 group-hover:opacity-100 group-hover:translate-y-0 translate-y-6 scale-95 group-hover:scale-100 transition-all duration-300 ease-in-out bg-white/70 backdrop-blur-md py-3 rounded-b-xl">
                                                                                <Link to={`/product/${product._id}`} className="block mt-2 text-center text-blue-500 hover:underline">
                                                                                        View Product
                                                                                </Link>
                                                                                <button onClick={() => handleAddToCart(product._id)} className="px-5 py-2 text-sm font-semibold uppercase tracking-wide text-white bg-amber-500 rounded-md shadow-md hover:bg-amber-600 transition-all">
                                                                                        Add to Cart
                                                                                </button>
                                                                        </div>
                                                                </div>

                                                                {/* Product Info */}
                                                                <div className="text-center py-4 bg-white">
                                                                        <h3 className="text-lg font-bold text-gray-800">{product.name}</h3>
                                                                        <p className="mt-1 text-rose-600 text-lg font-semibold">{product.price}</p>
                                                                </div>
                                                        </div>



                                                        // 




                                                ))
                                        ) : (
                                                <p className="text-center col-span-3 text-gray-500">Rendering Jewels for you.</p>
                                        )}
                                </div>
                        </main>
                </div>
        );
};

export default ProductListing;
