import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const [message, setMessage] = useState(""); // State for feedback messages
        const [reviews, setReviews] = useState([]);

        // temporary
        // const reviews = [
        //         {
        //                 id: 1,
        //                 user: "John Doe",
        //                 rating: 5,
        //                 comment: "Amazing product! Totally worth the price.",
        //                 date: "2024-03-07",
        //         },
        //         {
        //                 id: 2,
        //                 user: "Jane Smith",
        //                 rating: 4,
        //                 comment: "Great quality, but the delivery took longer than expected.",
        //                 date: "2024-03-06",
        //         },
        //         {
        //                 id: 3,
        //                 user: "David Johnson",
        //                 rating: 3,
        //                 comment: "Good, but expected better packaging.",
        //                 date: "2024-03-05",
        //         },
        // ];


        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError(data.message || "Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        } finally {
                                setLoading(false);
                        }
                };

                const fetchReviews = async () => {
                        try {
                                const response = await axios.get(`http://localhost:5000/api/reviews/product/${id}`);
                                setReviews(response.data);
                        } catch (err) {
                                setError(err.response?.data?.message || "Error fetching reviews.");
                        }
                };

                fetchProduct();
                fetchReviews();
        }, [id]);

        // Function to add product to cart
        const handleAddToCart = async () => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        hideMessage();
                        return;
                }

                console.log("Sending request to add product:", { userId, productId: id });

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();
                        console.log("Response from server:", data);

                        if (response.ok) {
                                setMessage("✅ Added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                hideMessage();
        };

        const handleAddToWishlist = async () => {
                const token = localStorage.getItem("userToken");

                if (!token) {
                        setMessage("⚠️ Please log in to add items to your wishlist.");
                        hideMessage();
                        return;
                }

                try {
                        const response = await axios.post(
                                `http://localhost:5000/api/wishlist/add/${id}`,
                                {},
                                {
                                        headers: { Authorization: `Bearer ${token}` },
                                }
                        );

                        console.log("Wishlist response:", response.data);
                        setMessage("❤️ Added to wishlist!");
                } catch (error) {
                        setMessage(`❌ ${error.response?.data?.message || "Failed to add to wishlist."}`);
                }

                hideMessage();
        };

        // Function to hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };

        const products = [
                { id: 1, name: "Product 1", price: "$99.99", image: "/product_images/product-1.webp" },
                { id: 2, name: "Product 2", price: "$109.99", image: "/product_images/product-2.webp" },
                { id: 3, name: "Product 3", price: "$89.99", image: "/product_images/product-3.webp" },
                { id: 4, name: "Product 4", price: "$79.99", image: "/product_images/product-4.webp" },
        ];


        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="relative min-h-screen bg-[#f7f3ee] overflow-hidden px-6 py-[70px]">
                        {product && (
                                <div className="bg-white] mx-auto shadow-xl rounded-tr-3xl rounded-tl-3xl overflow-hidden flex flex-col lg:flex-row gap-8 pt-[50px] p-6">
                                        {/* Product Image Section */}
                                        <div className="lg:w-1/2 relative flex items-center justify-center">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full h-auto object-cover rounded-2xl shadow-lg"
                                                />
                                                <span className="absolute top-4 left-4 bg-[#E4A5A5] text-white px-6 py-3 rounded-full text-sm shadow-md">
                                                        Luxury Collection
                                                </span>
                                        </div>

                                        {/* Product Details Section */}
                                        <div className="lg:w-1/2 flex flex-col justify-between space-y-6">
                                                <div>
                                                        <h2 className="text-4xl font-bold text-gray-900 tracking-wide">{product.name}</h2>
                                                        <p className="text-lg text-gray-600 mt-4 leading-relaxed">{product.description}</p>
                                                        <div className="mt-6">
                                                                <span className="text-3xl font-semibold text-[#E4A5A5]">${product.price}</span>
                                                        </div>
                                                </div>

                                                {/* Action Buttons */}
                                                <div className="mt-6 flex flex-col sm:flex-row sm:space-x-4">
                                                        <button
                                                                onClick={handleAddToCart}
                                                                className="bg-[#E4A5A5] text-white py-4 px-10 rounded-full shadow-md hover:bg-opacity-90 transition-all">
                                                                Add to Cart
                                                        </button>
                                                        <button
                                                                onClick={handleAddToWishlist}
                                                                className="mt-4 sm:mt-0 border-2 border-[#E4A5A5] text-[#E4A5A5] py-4 px-10 rounded-full hover:bg-[#E4A5A5] hover:text-white transition-all">
                                                                Add to Wishlist
                                                        </button>
                                                </div>
                                                {message && <p className="text-green-600 mt-4">{message}</p>}
                                        </div>
                                </div>
                        )}

                        {/* Category Description Section */}
                        <div className="px-8 py-12 bg-gradient-to-r from-gray-100 to-gray-200 rounded-bl-3xl rounded-br-3xl shadow-md">
                                <h3 className="text-3xl font-bold text-gray-900 text-start">Category Description</h3>
                                <p className="mt-4 text-lg text-gray-700 text-start leading-relaxed">
                                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed nec lorem at neque elementum auctor ac eget felis.
                                        Explore a curated selection of premium products designed to fit your lifestyle.
                                </p>
                        </div>

                        {/* Product Reviews Section */}
                        // Replace the entire Product Reviews Section with this:
                        <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-lg mt-10">
                                <div className="flex items-center justify-between mb-6">
                                        <h2 className="text-2xl font-bold text-gray-800">Customer Reviews</h2>
                                        <div className="text-right">
                                                <p className="text-3xl font-bold text-[#E4A5A5]">
                                                        {product.rating?.toFixed(1) || 0}
                                                </p>
                                                <p className="text-sm text-gray-600">
                                                        {product.numReviews} reviews
                                                </p>
                                        </div>
                                </div>

                                {/* Rating Breakdown */}
                                <div className="mb-8 grid grid-cols-2 gap-4 md:grid-cols-5">
                                        {[5, 4, 3, 2, 1].map((star) => (
                                                <div key={star} className="flex items-center gap-2">
                                                        <span className="text-sm w-8">{star} Star</span>
                                                        <div className="flex-1 h-2 bg-gray-200 rounded-full">
                                                                <div
                                                                        className="h-full bg-[#E4A5A5] rounded-full"
                                                                        style={{
                                                                                width: `${(reviews.filter(r => r.rating === star).length / reviews.length) * 100}%`
                                                                        }}
                                                                ></div>
                                                        </div>
                                                </div>
                                        ))}
                                </div>

                                {/* Reviews List */}
                                {reviews.length > 0 ? (
                                        reviews.map((review) => (
                                                <div key={review._id} className="border-b border-gray-200 py-4">
                                                        {/* Existing review content */}
                                                </div>
                                        ))
                                ) : (
                                        <p className="text-gray-500">No reviews yet. Be the first to review this product!</p>
                                )}
                        </div>


                        {/* Featured Products */}
                        <div className="py-20">
                                <div className="min-w-screen mx-auto px-6 sm:px-8 lg:px-10">
                                        <h2 className="text-4xl font-bold text-[#b76e79] text-center tracking-wide">
                                                Featured Products
                                        </h2>
                                        <div className="mt-12 grid gap-12 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                                                {products.map((product) => (
                                                        <div key={product.id} className="relative bg-white rounded-2xl shadow-lg border border-[#e0d6cc] overflow-hidden group transition-all duration-500 hover:shadow-2xl hover:-translate-y-3">

                                                                {/* Product Image with 3D Hover Effect */}
                                                                <div className="relative w-full overflow-hidden flex justify-center">
                                                                        <div className="relative w-full h-64 flex justify-center items-center">
                                                                                <img
                                                                                        src={product.image}
                                                                                        alt={product.name}
                                                                                        className="w-full h-64 object-cover transition-all duration-500 transform group-hover:scale-110 group-hover:-translate-y-2 group-hover:rotate-2 group-hover:shadow-xl"
                                                                                />
                                                                                {/* Soft Glow Effect */}
                                                                                <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-all duration-500"></div>
                                                                        </div>

                                                                        {/* Floating Buttons on Hover - FIXED */}
                                                                        <div className="absolute bottom-0 left-0 right-0 flex items-center justify-center space-x-4 opacity-0 group-hover:opacity-100 scale-95 group-hover:scale-100 transition-all duration-500 ease-in-out bg-white/90 backdrop-blur-lg py-3 shadow-md z-20 before:absolute before:inset-0 before:bg-gradient-to-t before:from-white before:to-transparent before:h-full before:w-full before:z-[-1]">
                                                                                <button className="px-5 py-2 text-sm font-semibold uppercase tracking-wide text-white bg-[#b76e79] rounded-md shadow-md hover:bg-[#9d5a64] transition">
                                                                                        View
                                                                                </button>
                                                                                <button className="px-5 py-2 text-sm font-semibold uppercase tracking-wide text-white bg-amber-500 rounded-md shadow-md hover:bg-amber-600 transition">
                                                                                        Add to Cart
                                                                                </button>
                                                                        </div>


                                                                </div>

                                                                {/* Product Info */}
                                                                <div className="text-center py-5 bg-white relative z-10">
                                                                        <h3 className="text-xl font-semibold text-gray-900">{product.name}</h3>
                                                                        <p className="mt-2 text-[#b76e79] text-lg font-bold">{product.price}</p>
                                                                </div>


                                                        </div>
                                                ))}
                                        </div>
                                </div>
                        </div>
                </div>

        );

};

export default SingleProduct;


