import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaFacebook, FaTwitter, FaInstagram } from "react-icons/fa";
import { FiUser } from "react-icons/fi";
import { AiOutlineShoppingCart } from "react-icons/ai";
import { BsBox } from "react-icons/bs";
import { Menu, X } from "lucide-react";

const Navbar = () => {
        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isOpen, setIsOpen] = useState(false);
        const [profileOpen, setProfileOpen] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);

        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token); // Convert token existence to boolean
                };

                checkAuth();
                window.addEventListener("storage", checkAuth); // Update on login/logout

                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        return (
                <nav className="bg-white shadow-md dark:bg-gray-900">
                        {/* Top Bar */}
                        <div className="bg-rose-100 dark:bg-gray-800 text-gray-700 dark:text-gray-200 py-2 px-6 flex justify-between text-sm">
                                <div>
                                        <span className="mr-4">📞 +123 456 7890</span>
                                        <span>✉️ contact@jewelstore.com</span>
                                </div>
                                <div className="flex space-x-4">
                                        <a href="#" className="hover:text-rose-600"><FaFacebook /></a>
                                        <a href="#" className="hover:text-rose-600"><FaTwitter /></a>
                                        <a href="#" className="hover:text-rose-600"><FaInstagram /></a>
                                </div>
                        </div>

                        {/* Main Navbar */}
                        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between relative">
                                {/* Left - Nav Links */}
                                <div className="hidden md:flex space-x-6 text-gray-800 dark:text-gray-200">
                                        <Link to="/" className="hover:text-rose-600">Home</Link>
                                        <Link to="/contact" className="hover:text-rose-600">Contact</Link>
                                        <Link to="/products" className="hover:text-rose-600">Products</Link>
                                </div>

                                {/* Center - Logo */}
                                <Link to="/" className="text-3xl font-bold text-gray-900 dark:text-white">
                                        Jewel<span className="text-rose-600">Store</span>
                                </Link>

                                {/* Right - Icons */}
                                <div className="flex items-center space-x-4">
                                        {isAuthenticated && (
                                                <>
                                                        {/* Profile Dropdown */}
                                                        <div className="relative">
                                                                <button onClick={() => setProfileOpen(!profileOpen)} className="hover:text-rose-600">
                                                                <FiUser />
                                                                </button>
                                                                {profileOpen && (
                                                                        <div className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-800 shadow-lg rounded-md py-2">
                                                                                <Link to="/profile" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Profile</Link>
                                                                                <Link to="/wishlist" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Wishlist</Link>
                                                                                <button onClick={handleLogout} className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">
                                                                                        Logout
                                                                                </button>
                                                                        </div>
                                                                )}
                                                        </div>

                                                        {/* Cart */}
                                                        <Link to="/cart" className="hover:text-rose-600">
                                                        <AiOutlineShoppingCart />
                                                        </Link>

                                                        {/* Orders */}
                                                        <Link to="/order-history" className="hover:text-rose-600">
                                                        <BsBox />
                                                        </Link>
                                                </>
                                        )}
                                </div>

                                {/* Mobile Menu Button */}
                                <button className="md:hidden" onClick={() => setIsOpen(!isOpen)}>
                                        {isOpen ? <X size={28} /> : <Menu size={28} />}
                                </button>
                        </div>

                        {/* Mobile Menu */}
                        {isOpen && (
                                <div className="md:hidden bg-white dark:bg-gray-900 shadow-lg p-4 flex flex-col space-y-3">
                                        <Link className="hover:text-rose-600" to="/">Home</Link>
                                        <Link className="hover:text-rose-600" to="/contact">Contact</Link>
                                        <Link className="hover:text-rose-600" to="/products">Products</Link>
                                        {isAuthenticated && (
                                                <>
                                                        <Link className="hover:text-rose-600" to="/cart">Cart</Link>
                                                        <Link className="hover:text-rose-600" to="/wishlist">Wishlist</Link>
                                                        <Link className="hover:text-rose-600" to="/order-history">Orders</Link>
                                                        <Link className="hover:text-rose-600" to="/profile">Profile</Link>
                                                        <button onClick={handleLogout} className="px-4 py-2 bg-red-600 text-white rounded-md">Logout</button>
                                                </>
                                        )}
                                </div>
                        )}
                </nav>
        );
};

export default Navbar;
