import { useLocation, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const ReviewPage = () => {
        const location = useLocation();
        const navigate = useNavigate();
        const order = location?.state?.order;

        const [reviews, setReviews] = useState(
                order?.items?.map((item) => ({ product: item.id, rating: 0, comment: "" })) || []
        );

        if (!order) {
                return <p className="text-center text-red-500">No order found.</p>;
        }

        const handleReviewChange = (index, field, value) => {
                const newReviews = [...reviews];
                newReviews[index][field] = value;
                setReviews(newReviews);
        };

        const submitReview = async () => {
                const token = localStorage.getItem("token");

                if (!token) {
                        alert("Authentication required. Please log in.");
                        return;
                }

                axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

                try {
                        const response = await axios.post("http://localhost:5000/api/reviews", { reviews });

                        if (response.status === 201 || response.status === 200) {
                                alert("Reviews submitted successfully!");
                                navigate(-1);
                        }
                } catch (error) {
                        console.error("Error submitting reviews:", error);

                        const errorMessage =
                                error.response?.data?.message || "Failed to submit reviews. Please try again.";
                        alert(errorMessage);
                }
        };

        return (
                <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg shadow-xl mt-10">
                        <h2 className="text-2xl font-bold text-center mb-6">Write a Review</h2>

                        {order.items.map((item, index) => (
                                <div key={item.id} className="border-b pb-6 mb-6">
                                        <div className="flex items-center gap-4">
                                                <img src="https://via.placeholder.com/80" alt={item.name} className="w-20 h-20 rounded-lg" />
                                                <h3 className="text-lg font-semibold">{item.name}</h3>
                                        </div>

                                        <div className="mt-4">
                                                <label className="block font-semibold">Rating:</label>
                                                <select
                                                        value={reviews[index]?.rating}
                                                        onChange={(e) => handleReviewChange(index, "rating", Number(e.target.value))}
                                                        className="w-full p-2 border rounded-lg"
                                                >
                                                        <option value="0">Select Rating</option>
                                                        {[1, 2, 3, 4, 5].map((num) => (
                                                                <option key={num} value={num}>
                                                                        {num} Star
                                                                </option>
                                                        ))}
                                                </select>
                                        </div>

                                        <div className="mt-4">
                                                <label className="block font-semibold">Comment:</label>
                                                <textarea
                                                        value={reviews[index]?.comment}
                                                        onChange={(e) => handleReviewChange(index, "comment", e.target.value)}
                                                        className="w-full p-2 border rounded-lg"
                                                        placeholder="Write your review here..."
                                                />
                                        </div>
                                </div>
                        ))}

                        <div className="flex justify-between mt-6">
                                <button onClick={() => navigate(-1)} className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600">
                                        Go Back
                                </button>

                                <button onClick={submitReview} className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700">
                                        Submit Reviews
                                </button>
                        </div>
                </div>
        );
};

export default ReviewPage;
