import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Cart = () => {
        const [cartItems, setCartItems] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const navigate = useNavigate();
        const userId = localStorage.getItem("userId");
        const token = localStorage.getItem("userToken");

        const fetchCart = async () => {
                try {
                        const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                                method: "GET",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                        });

                        const data = await response.json();

                        if (response.ok) {
                                setCartItems(data.products || []);
                        } else {
                                setError(data.message || "Failed to load cart.");
                        }
                } catch (err) {
                        console.error("Fetch error:", err);
                        setError("Server error while fetching cart.");
                } finally {
                        setLoading(false);
                }
        };

        useEffect(() => {
                if (userId && token) {
                        fetchCart();
                }
        }, [userId, token]);

        const updateQuantity = async (id, amount) => {
                const newQuantity = cartItems.find((item) => item.product._id === id)?.quantity + amount;
                if (newQuantity < 1) return removeItem(id);

                try {
                        await fetch("http://localhost:5000/api/cart/update", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id, quantity: newQuantity }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Update error:", err);
                }
        };

        const removeItem = async (id) => {
                try {
                        await fetch("http://localhost:5000/api/cart/remove", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });
                        fetchCart();
                } catch (err) {
                        console.error("Remove error:", err);
                }
        };

        const handleCheckout = () => {
                navigate("/checkout", {
                        state: {
                                cartItems,
                                subtotal,
                                tax,
                                shipping,
                                total
                        }
                });
        };


        const subtotal = cartItems.reduce((acc, item) => acc + item.product.price * item.quantity, 0);
        const tax = subtotal * 0.1;
        const shipping = subtotal > 0 ? 10 : 0;
        const total = subtotal + tax + shipping;

        if (loading) return <div>Loading...</div>;
        if (error) return <div className="text-red-600">{error}</div>;

        return (
                <div className="max-w-5xl mx-auto p-8 bg-[#FAF5F0] shadow-xl rounded-2xl mt-12 grid grid-cols-1 md:grid-cols-3 gap-8" >
                        {/* Shopping Cart Section */}
                        < div className="md:col-span-2" >
                                <h2 className="text-3xl font-bold text-[#6B4E4D] mb-6">🛒 Your Shopping Cart</h2>

                                {
                                        cartItems.length === 0 ? (
                                                <p className="text-gray-500 text-lg">Your cart is empty.</p>
                                        ) : (
                                                <div className="space-y-6">
                                                        {cartItems.map((item) => (
                                                                <div key={item.product._id} className="flex justify-between items-center p-5 bg-white rounded-lg shadow-md">
                                                                        {/* Product Image & Details */}
                                                                        <div className="flex items-center gap-5">
                                                                                <img src={item.product.image} alt={item.product.name} className="w-20 h-20 object-cover rounded-xl border border-[#D8B6A4]" />
                                                                                <div>
                                                                                        <h3 className="text-lg font-semibold text-[#6B4E4D]">{item.product.name}</h3>
                                                                                        <p className="text-gray-600 text-sm">${item.product.price.toFixed(2)}</p>
                                                                                </div>
                                                                        </div>

                                                                        {/* Quantity Control */}
                                                                        <div className="flex items-center gap-3 bg-[#F3E5DC] px-4 py-2 rounded-lg">
                                                                                <button onClick={() => updateQuantity(item.product._id, -1)} className="text-xl text-[#6B4E4D] px-3 hover:bg-[#D8B6A4] rounded transition">−</button>
                                                                                <span className="text-lg font-semibold">{item.quantity}</span>
                                                                                <button onClick={() => updateQuantity(item.product._id, 1)} className="text-xl text-[#6B4E4D] px-3 hover:bg-[#D8B6A4] rounded transition">+</button>
                                                                        </div>

                                                                        {/* Price & Remove Button */}
                                                                        <div className="text-center">
                                                                                <p className="text-lg font-semibold text-[#6B4E4D]">${(item.product.price * item.quantity).toFixed(2)}</p>
                                                                                <button onClick={() => removeItem(item.product._id)} className="mt-2 text-sm text-red-500 hover:text-red-700">Remove</button>
                                                                        </div>
                                                                </div>
                                                        ))}
                                                </div>
                                        )
                                }
                        </div >

                        {/* Cart Summary Section */}
                        < div className="p-6 bg-[#F9ECE3] rounded-xl shadow-lg" >
                                <h3 className="text-2xl font-semibold text-[#6B4E4D] mb-4">🧾 Order Summary</h3>
                                <div className="space-y-2">
                                        <div className="flex justify-between"><span>Subtotal:</span><span className="font-semibold">${subtotal.toFixed(2)}</span></div>
                                        <div className="flex justify-between"><span>Tax (10%):</span><span className="font-semibold">${tax.toFixed(2)}</span></div>
                                        <div className="flex justify-between"><span>Shipping:</span><span className="font-semibold">${shipping.toFixed(2)}</span></div>
                                </div>
                                <hr className="my-4 border-[#D8B6A4]" />
                                <div className="flex justify-between text-lg font-bold text-[#6B4E4D]">
                                        <span>Total:</span><span>${total.toFixed(2)}</span>
                                </div>

                                {/* Checkout Button */}
                                <button
                                        className={`w-full mt-6 py-3 rounded-lg font-semibold text-lg transition duration-300 ${total === 0 ? "bg-gray-400 cursor-not-allowed" : "bg-[#B88E8D] hover:bg-[#A67D7C] text-white"
                                                }`}
                                        disabled={total === 0}
                                        onClick={handleCheckout}
                                >
                                        Proceed to Checkout
                                </button>
                        </div >
                </div >
        );
};

export default Cart;
