/**
 * Interface for handling any currency
 * @example
 * const paymentAmount = {
 *    "amount": 100,
 *    "currencyCode": "USD"
 * }
 */
interface Money {
    /**
     * An integer representing the amount in the smallest unit of currency for a given locale,
     * scaled by the standard number of decimal places for that currency. For example, `amount = 100`
     * in USD means `$1.00`.
     */
    amount: number;
    /**
     * 3-letter ISO 4217 currency code.
     */
    currencyCode: string;
}
export { Money };
